        &lt;span class="kt"&gt;double&lt;/span&gt; &lt;span class="n"&gt;cropH&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="n"&gt;cropB&lt;/span&gt; &lt;span class="p"&gt;-&lt;/span&gt; &lt;span class="n"&gt;cropT&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;

