&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;dict&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;title&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;title&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;keyword&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;keyword&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;city&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;city&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;state&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;state&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;imageUrl&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageUrl&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;imageAlt&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageAlt&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;page&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;dict&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;content&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;content&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;kind&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;page&amp;#34;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;params&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;path&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;slug&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;title&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;title&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;AddPage&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;page&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;A few things to point out here. First, as we’re inside a Range (where the context is our remote data), we’ll need to prefix the &lt;code&gt;.AddPage&lt;/code&gt; method with a &lt;code&gt;$&lt;/code&gt;. This gives us access to Page context from within our range. Now we have access to the &lt;code&gt;.AddPage&lt;/code&gt; method.&lt;/p&gt;
&lt;p&gt;Notice in our &lt;code&gt;$page&lt;/code&gt; dict, we’re also including a value for &lt;code&gt;path&lt;/code&gt;. This is a require field, so don’t forget it. Hugo needs it to &lt;a href="https://gohugo.io/content-management/content-adapters/#page-map" target="_blank" rel="noopener"&gt;generate the logical path&lt;/a&gt; of pages relative to where the content adapter is set up. In my sheet, this path is stored in &lt;code&gt;$slug&lt;/code&gt;. The Hugo docs also recommend including the page &lt;code&gt;kind&lt;/code&gt; and a &lt;code&gt;title&lt;/code&gt; for each page.&lt;/p&gt;
&lt;h3 id="passing-media-resources-for-image-processing"&gt;Passing media resources for image processing&lt;/h3&gt;
&lt;p&gt;If you wish to process your image content using Hugo’s &lt;a href="https://gohugo.io/content-management/image-processing/" target="_blank" rel="noopener"&gt;Image processing&lt;/a&gt; capabilities, then, in a similar way as before, you will need to map your image URL, image alt text, and so on, to the &lt;a href="https://gohugo.io/content-management/content-adapters/#resource-map" target="_blank" rel="noopener"&gt;AddResource&lt;/a&gt; method.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;resources&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;GetRemote&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageUrl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;dict&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;mediaType&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;MediaType&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Type&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;value&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;dict&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;alt&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageAlt&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;resource&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;dict&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;content&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;content&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;params&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;path&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nx"&gt;printf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;%s/cover.%s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;slug&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;MediaType&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;SubType&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;AddResource&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;resource&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;As before, first we open up a With statement and use the GetRemote function to get hold of our indexed image URL. Next we set up a map for image content. Notice that for &lt;em&gt;mediaType&lt;/em&gt;, we’re now using&lt;code&gt;.MediaType.Type&lt;/code&gt; and passing &lt;code&gt;.Content&lt;/code&gt; as the &lt;em&gt;value&lt;/em&gt;. Then we set up another map for our Params. In this case, I only have an image alt. Both these maps then get passed to our &lt;code&gt;$resource&lt;/code&gt; map, where we also build the &lt;code&gt;path&lt;/code&gt; to the resource. Our indexed &lt;code&gt;$slug&lt;/code&gt; builds the first part of the path, then we give the resource itself the name cover and use &lt;code&gt;.MediaType.SubType&lt;/code&gt; to define the image format.&lt;/p&gt;
&lt;p&gt;With that, our content adapter is set up and ready to generate pages.&lt;/p&gt;
&lt;p&gt;At this point, you probably will have realised that content adapters don’t create the page layout itself. All they do is generate the pages and pass the associated data ready to use in a layout template.&lt;/p&gt;
&lt;p&gt;The next step, then, is to build a layout template for our pages, so that we can render our page content on each page. Let’s look at that next.&lt;/p&gt;
&lt;h2 id="build-a-singlehtml-layout-template-to-render-the-data"&gt;Build a &lt;code&gt;single.html&lt;/code&gt; layout template to render the data&lt;/h2&gt;
&lt;p&gt;As we’re generating single pages with our content adapter, we’ll need to create a &lt;code&gt;single.html&lt;/code&gt; layout template. For the template to work, we need to make sure it lives is a folder named after the folder where the content adapter can be found within the content folder.&lt;/p&gt;
&lt;p&gt;My content adapter lives is inside a &lt;a href="https://gohugo.io/content-management/sections/" target="_blank" rel="noopener"&gt;section&lt;/a&gt; (a subdirectory of the content folder) called &lt;em&gt;locations&lt;/em&gt;. So I’ll create a folder in the layouts directory that’s also called &lt;code&gt;locations&lt;/code&gt;. Next I’ll create the &lt;code&gt;single.html&lt;/code&gt; template inside this new folder.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;layouts/
locations/
single.html
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Now we have a layout template for our content adapter, we’re ready to pull the data into the template and render our pages.&lt;/p&gt;
&lt;h3 id="setting-up-variables-to-store-your-data"&gt;Setting up variables to store your data&lt;/h3&gt;
&lt;p&gt;I like to set up variables at the very top of my templates. These variables hold any data used in the template. That way I can easily access that data regardless of the current context.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;define&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;main&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;keyword&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;keyword&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;city&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;city&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;state&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;state&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageUrl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;imageurl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageAlt&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;imagealt&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;In our content adapter, we mapped the &lt;em&gt;title&lt;/em&gt; and &lt;em&gt;content&lt;/em&gt; of our pages to the .&lt;code&gt;Title&lt;/code&gt; and &lt;code&gt;.Content&lt;/code&gt; methods, respectively. The rest of the data we mapped to the Page object as custom parameters. That means we can access it using the &lt;a href="https://gohugo.io/methods/site/params/" target="_blank" rel="noopener"&gt;Params&lt;/a&gt; method.&lt;/p&gt;
&lt;p&gt;Given that it’s not always easy to remember the shape of your data, the &lt;a href="https://gohugo.io/functions/debug/dump/" target="_blank" rel="noopener"&gt;debug.Dump&lt;/a&gt; function can be useful in this situation.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;pre&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ debug.Dump .Params }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;pre&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Pass in the Dot or &lt;code&gt;.Params&lt;/code&gt; to see a nice JSON object of the available data.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-json" data-lang="json"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;city&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;imagealt&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;Roofsimple GAF Moisture Barrier&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;imageurl&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;https://res.cloudinary.com/roofsimple/image/upload/f_jpg,w_1400/Moisture_Barrier_2400_x_1600_ez3uj1.jpg&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;keyword&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;Virginia Roofing Company&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;publish&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;yes&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;state&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;Virginia&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;&amp;#34;title&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;The Best Virginia Roofing Company&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;This prevents the need to constantly head back to your content adapter to remind yourself how you named your Params.&lt;/p&gt;
&lt;h3 id="building-your-page-layout"&gt;Building your page layout&lt;/h3&gt;
&lt;p&gt;Now our Page data is conveniently stored in variables, we can use these variables to build our page layout, as required.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;define&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;main&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;keyword&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;keyword&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;city&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;city&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;state&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;state&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageUrl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;imageurl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageAlt&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;imagealt&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;&amp;lt;!-- Image as static asset --&amp;gt;&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;safeHTML&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;img&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;src&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .Params.imageUrl }}&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;alt&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .Params.imageAlt }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="nx"&gt;City&lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;:&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;city&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="nx"&gt;State&lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;:&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;state&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="nx"&gt;Keyword&lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;:&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;keyword&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="nx"&gt;Image&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;URL&lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;:&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageUrl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="nx"&gt;Image&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;Alt&lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;strong&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;:&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;imageAlt&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I’m sure your templating needs will be more sophisticated than the example above, but hopefully you get the idea.&lt;/p&gt;
&lt;p&gt;Notice that image data passed via Params can only be used &lt;em&gt;as-is&lt;/em&gt;, without image processing. However, any media passed &lt;a href="https://harrycresswell.com/#passing-media-resources-for-image-processing"&gt;using the AddResource method&lt;/a&gt;, can be fetched with the &lt;a href="https://gohugo.io/methods/page/resources/" target="_blank" rel="noopener"&gt;resources method&lt;/a&gt; and manipulated using &lt;a href="https://gohugo.io/content-management/image-processing/" target="_blank" rel="noopener"&gt;Image processing&lt;/a&gt;. Just as you might do with any other page resource found inside a page bundle.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;&amp;lt;!-- Image as a Page resource --&amp;gt;&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;safeHTML&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;resourceImage&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Resources&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;GetMatch&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;cover.*&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;resourceImage&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;image&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;resourceImage&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Process&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;crop 600x400&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;img&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;src&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ $image.RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;width&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ $image.Width }}&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;height&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ $image.Height }}&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;alt&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ $image.Params.alt }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;It’s worth pointing out that templating pages generated using content adapters doesn’t end with the &lt;code&gt;single.html&lt;/code&gt; template. You can also create a &lt;code&gt;list.html&lt;/code&gt; template to design the layout of the list view your pages inherit. This works exactly as you’d expect, so in the interest of brevity, I’ll leave that for you to explore further.&lt;/p&gt;
&lt;h2 id="dealing-with-final-challenges"&gt;Dealing with final challenges&lt;/h2&gt;
&lt;p&gt;Before I wrap this one up, I’ll briefly explain how we solved the challenges this particularly project presented, which I touched on earlier.&lt;/p&gt;
&lt;h3 id="adding-cache-busting-to-refresh-the-data-with-every-site-build"&gt;Adding cache busting to refresh the data with every site build&lt;/h3&gt;
&lt;p&gt;As mentioned, my client needed the ability to update each page, directly from the sheet where the data is stored. Essentially, using Google Sheets as a content management system.&lt;/p&gt;
&lt;p&gt;The main challenge with making regular updates via a Sheet is that Google caches requests to Sheets. This means that if we keep making requests to the same URL, then any updates made to the sheet won’t necessarily be visible and we’ll continue to see the previously cached data.&lt;/p&gt;
&lt;p&gt;The solution is to cache bust the sheet by appending a random string to the original URL. This creates a unique request and pulls in the latest data from the sheet.&lt;/p&gt;
&lt;p&gt;Consider the following updates to my &lt;code&gt;_content.gotmpl&lt;/code&gt; file:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;seed&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;now&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Unix&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;randUrl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nx"&gt;printf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;&amp;amp;rando=%s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nx"&gt;printf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;%.10s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nx"&gt;sha256&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;seed&lt;/span&gt;&lt;span class="p"&gt;))&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;printf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;%s%s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;url&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;printf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;resources&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;GetRemote&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;randUrl&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Err&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;errorf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;Unable to get remote resource %s: %s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;url&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;else&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;data&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;transform&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Unmarshal&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;else&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;errorf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;Unable to get remote resource %s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;url&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;First we create a &lt;em&gt;seed&lt;/em&gt; using the &lt;a href="https://gohugo.io/functions/time/now/" target="_blank" rel="noopener"&gt;time.Now&lt;/a&gt; function. This returns the current time as a Unix timestamp, which for our purposes provides a random set of numbers we can work with. Next we pass our &lt;code&gt;$seed&lt;/code&gt; value to the &lt;a href="https://gohugo.io/functions/crypto/sha256/" target="_blank" rel="noopener"&gt;crypto.SHA256&lt;/a&gt; function. This generates a random string which we append to the original URL provided by Google Sheets. This random string is stored in our &lt;code&gt;$randUrl&lt;/code&gt; variable. Finally, we pass &lt;code&gt;$randUrl&lt;/code&gt; to the GetRemote function instead of the original URL.&lt;/p&gt;
&lt;p&gt;For more on this method, check out &lt;a href="https://www.youtube.com/watch?v=_PlDMok-yUg" target="_blank" rel="noopener"&gt;How to use Google Sheets as source data for Hugo SSG content&lt;/a&gt;. Bryan explains this stuff far better than I ever could.&lt;/p&gt;
&lt;h3 id="conditionally-generate-pages"&gt;Conditionally generate pages&lt;/h3&gt;
&lt;p&gt;The second challenge we faced during this project was figuring out a way to control whether pages publish on the live website, or not.&lt;/p&gt;
&lt;p&gt;The rather rudimentary solution we came up with was to add a &lt;em&gt;Publish&lt;/em&gt; column to the sheet, where possible values are either &lt;em&gt;yes&lt;/em&gt; or &lt;em&gt;no&lt;/em&gt;. With this we could create a condition in the &lt;em&gt;_content.gotmpl&lt;/em&gt;, where the AddPage function only runs (and so creates the page) &lt;em&gt;if&lt;/em&gt; the value of &lt;code&gt;$publish&lt;/code&gt; is &lt;em&gt;true&lt;/em&gt;.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;if&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;eq&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;publish&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;yes&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;AddPage&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;page&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;In case you had wondered, this explains why the following line of code exists within the Range function we use to loop through the data in the sheet.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;publish&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;index&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="mi"&gt;6&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Perhaps there’s a better way to approach this problem, but it’s effective and work just fine for now.&lt;/p&gt;
&lt;h3 id="triggering-a-site-build-from-google-sheets"&gt;Triggering a site build from Google Sheets&lt;/h3&gt;
&lt;p&gt;The final challenge was to create an easy way for the client to trigger a site build once they’ve made changes to the Google Sheet. This is a crucial step, as without it we wouldn’t be able to generate new pages or update any existing content on the site, as data in the sheet changes.&lt;/p&gt;
&lt;p&gt;Getting this set up and working involved the following steps:&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;Create a button in the sheet.&lt;/li&gt;
&lt;li&gt;Create a script that triggers a build.&lt;/li&gt;
&lt;li&gt;Assign the script to your button.&lt;/li&gt;
&lt;li&gt;Set up a build hook with your deployment service (Netlify etc).&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;Spreadsheet.dev has &lt;a href="https://spreadsheet.dev/buttons-in-google-sheets" target="_blank" rel="noopener"&gt;a great post on creating buttons in a Google Sheets&lt;/a&gt; (step 1). This post also covers step 2 – how to create a script via &lt;strong&gt;Extensions →Apps Script&lt;/strong&gt; - and step 3 – assigning a script to the button.&lt;/p&gt;
&lt;p&gt;The script required to trigger the build will look something like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-javascript" data-lang="javascript"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="kd"&gt;function&lt;/span&gt; &lt;span class="nx"&gt;runBuild&lt;/span&gt;&lt;span class="p"&gt;()&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;UrlFetchApp&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;fetch&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;https://api.netlify.com/build_hooks/67af30cf8435ff6927017aef?clear_cache=true&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="s1"&gt;&amp;#39;method&amp;#39;&lt;/span&gt;&lt;span class="o"&gt;:&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;post&amp;#39;&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;});&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Where the URL in the script is the build hook provided by the deployment service (step 4), in this case Netlify.&lt;/p&gt;
&lt;p&gt;Keep in mind, when you first click your button, you will need to grant the required authorisation for the script to run. Following this, the script will run anytime the button is clicked, which will trigger the build hook and re-deploy the website.&lt;/p&gt;
&lt;h3 id="troubleshooting-issues"&gt;Troubleshooting issues&lt;/h3&gt;
&lt;p&gt;Debugging a content adapter isn’t always easy, as before you build a layout template, you won’t see any output or error messages in the browser.&lt;/p&gt;
&lt;p&gt;A quick and dirty way around this problem is to temporarily move your content adapter code to am existing layout template. Then use the &lt;a href="https://gohugo.io/troubleshooting/inspection/" target="_blank" rel="noopener"&gt;debug.Dump&lt;/a&gt; function again to inspect your data object.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;resources&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;GetRemote&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;url&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;with&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Err&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;errorf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;Unable to get remote resource %s: %s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;url&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;else&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;data&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;transform&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Unmarshal&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;pre&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;debug&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Dump&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;data&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;pre&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;else&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;errorf&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;Unable to get remote resource %s&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;url&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;While this won’t generate your pages, it will return the data object. From here you can figure out the shape of your data.&lt;/p&gt;
&lt;h2 id="wrapping-up"&gt;Wrapping up&lt;/h2&gt;
&lt;p&gt;Well done if you’ve made it this far. The good news is that you can now use Google Sheets as a CMS for your Hugo websites. Exciting times!&lt;/p&gt;
&lt;p&gt;I’ve put together a &lt;a href="https://github.com/harrycresswell/hugo-content-adapters" target="_blank" rel="noopener"&gt;repository&lt;/a&gt; and &lt;a href="https://hugo-content-adapters.netlify.app/" target="_blank" rel="noopener"&gt;demo site&lt;/a&gt; to accompany this post. Hopefully this will help you hit the ground running.&lt;/p&gt;
&lt;p&gt;I want to wrap this one up by pointing out an important distinction to make when building pages using content adapters. And, that is that these pages are &lt;em&gt;dynamically&lt;/em&gt; generated. In other words, content adapters will not create a bunch of markdown files in your content folder. This can feel a little strange at first, as, with Hugo, we’re used to working with markdown files and building pages statically. But, if you’re familiar with dynamic website tools, then this shouldn’t be too much to wrap your head around.&lt;/p&gt;
&lt;p&gt;If, for whatever reason, you do want to create markdown files from remote data sources, then I recommend looking into Regis Philibert’s &lt;a href="https://www.thenewdynamic.com/article/toward-using-a-headless-cms-with-hugo-part-2-building-from-remote-api/" target="_blank" rel="noopener"&gt;pre-build and mount method&lt;/a&gt;. I’ve been using this approach to generate Markdown files for Obsidian from Airtable data. It’s super useful, but more on that another day.&lt;/p&gt;
&lt;div class="footnotes" role="doc-endnotes"&gt;
&lt;hr&gt;
&lt;ol&gt;
&lt;li id="fn:1"&gt;
&lt;p&gt;This isn’t entirely true, as we did have some clever work arounds. Notably the &lt;a href="https://www.thenewdynamic.com/article/toward-using-a-headless-cms-with-hugo-part-2-building-from-remote-api/" target="_blank" rel="noopener"&gt;pre-build and mount method&lt;/a&gt; which I mention in the conclusion of this post.&amp;#160;&lt;a href="#fnref:1" class="footnote-backref" role="doc-backlink"&gt;&amp;#x21a9;&amp;#xfe0e;&lt;/a&gt;&lt;/p&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;/div&gt;

&lt;p&gt;There’s nothing worse than finishing work, feeling tired, then having to figure out what to cook for dinner. It’s a real pain and often leads to bad decisions.&lt;/p&gt;
&lt;p&gt;I’ve written before about &lt;a href="https://harrycresswell.com/writing/sunday-meal-prep/"&gt;how I use meal prepping to stay healthy&lt;/a&gt; throughout the week. In a similar vein, today I want to talk about how I go about planning meals, so we never have to think too hard about what to cook during the week.&lt;/p&gt;
&lt;p&gt;The key to planning what you eat, I’ve realised, starts by owning a handful of quality cookbooks, which contain healthy, easy-to-cook meals that taste great.&lt;/p&gt;
&lt;p&gt;My favourite cookbooks right now are:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;a href="https://ottolenghi.co.uk/products/flavour-cookbook" target="_blank" rel="noopener"&gt;Flavour by Ottolenghi&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.goodreads.com/book/show/43501560-one" target="_blank" rel="noopener"&gt;One pot, pan, planet by Anna Jones&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.goodreads.com/book/show/203017792-easy-wins" target="_blank" rel="noopener"&gt;Easy Wins by Anna Jones&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.goodreads.com/book/show/38882366-deliciously-ella" target="_blank" rel="noopener"&gt;The Plant-based Cookbook by Deliciously Ella&lt;/a&gt;&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.goodreads.com/book/show/52894119-deliciously-ella-making-plant-based-quick-and-easy" target="_blank" rel="noopener"&gt;Quick and Easy by Deliciously Ella&lt;/a&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;We usually buy our fruit and veg from the local market on Saturday mornings. So on either Friday evening or Saturday morning, we’ll have a quick look through our cookbooks, then pull out a handful of recipes that we fancy eating the following week.&lt;/p&gt;
&lt;p&gt;Using Apple Notes, one of us will note down the recipe name, the name of the cookbook and page number the recipe can be found on. Then we extract the ingredients for each recipe into Apple Reminders, where we keep a shared shopping list. Currently we do this manually, but you could easily use something like &lt;a href="https://support.apple.com/en-gb/HT212630" target="_blank" rel="noopener"&gt;iPhone’s Live Text feature&lt;/a&gt;, to take a picture of a recipe and have your phone extract the text. Once the ingredients are in our shopping list, we’re ready to head down to the market and grab what we need.&lt;/p&gt;
&lt;p&gt;Each evening, we’ll pick one of the recipes we’ve written down in Apple Notes to cook. Often recipes will serve at least four people, so that leaves us with plenty of leftovers to use for lunch the following day.&lt;/p&gt;
&lt;p&gt;What I love about this approach is that it takes the hassle out of figuring out what to eat every night. It’s super efficient too, as you generally only end up buying what you need for the week. No more dealing with forgotten vegetables rotting at the back of the fridge.&lt;/p&gt;
&lt;p&gt;On another note, it makes me wonder why services like Hello Fresh even exist. It’s fun to pick your own recipes and actually leave the house to buy your own ingredients. Just as Manu points out that &lt;a href="https://manuelmoreale.com/indieweb-carnival-on-the-importance-of-friction" target="_blank" rel="noopener"&gt;friction is important in the digital world&lt;/a&gt;, it’s also important in the physical world, too.&lt;/p&gt;

&lt;p&gt;Creating semantic headings can be tricky business. This is especially true when you find the need to render all the content of your pages on a list page (a page that lists pages). Often we only see page titles and summaries on a list page, which usually cause little to no issue. But, as soon as you throw a few h2’s and h3’s into the mix, correct heading heirachy tends to fly straight out the window.&lt;/p&gt;
&lt;p&gt;The solution I’ve found is to use a little Regex. But before we get into that, first let me give you an example to help illustrate the problem.&lt;/p&gt;
&lt;h2 id="the-problem"&gt;The problem&lt;/h2&gt;
&lt;p&gt;Below I have a basic markdown file containing a page title stored in YAML front matter and markdown content, mostly organised under level two headings (h2). When writing Markdown, level two headings are indicated by adding a double hash (##) before the heading. The reason we start with level two headings in the content, is that the title will take the top-level heading (h1). And, only one h1 can be used per page.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-md" data-lang="md"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;---
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;title: The title of our page
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;---
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;Some intro
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="gu"&gt;## Some heading level 2
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="gu"&gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;Some content
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="gu"&gt;## Another heading level 2
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="gu"&gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;More content
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Before we look at the list page, first lets consider a simple &lt;code&gt;single.html&lt;/code&gt; template that we might use, in a Hugo project, to render this page.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;define&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;main&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;class&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;flow&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;As you might expect, the output this &lt;code&gt;single.html&lt;/code&gt; template generates for our page presents no issues whatsoever. All headings are semantically correct.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;body&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt; &lt;span class="na"&gt;class&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;flow&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;The title of our page&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Some intro&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;A heading level 2&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Some content&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Another heading level 2&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;More content&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;body&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Now let’s consider the &lt;code&gt;list.html&lt;/code&gt; page template, that we might use to render our page alongside other pages within the same collection or section.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;define&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;main&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;range&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Pages&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;class&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;flow&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;The problem isn’t immediately obvious as we look at our list template. But, the output that our list template generates tells a different story.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Posts&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt; &lt;span class="na"&gt;class&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;flow&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;The title of our page&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Some intro&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;A heading level 2&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Some content&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Another heading level 2&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;More content&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt; &lt;span class="na"&gt;class&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;flow&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;The title of another page&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Some intro&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;A heading level 2&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Some content&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Another heading level 2&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;More content&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;You’ll notice that the &lt;code&gt;&amp;lt;h1&amp;gt;&lt;/code&gt; is now occupied by the title of our list page – in this case, &lt;em&gt;Posts&lt;/em&gt; – and no longer the title of our pages. This makes sense, as we want to describe the contents of the whole page, using the top-level heading.&lt;/p&gt;
&lt;p&gt;You’ll also notice that the title of our pages are now wrapped in &lt;code&gt;&amp;lt;h2&amp;gt;&lt;/code&gt; tags. This is semantically correct, as they are no longer the top-level heading like on a single page, but instead the level two subheadings of our list page. One indentation down, if you like, in the structure of content.&lt;/p&gt;
&lt;p&gt;Herein lies the problem. As our page titles are now &lt;code&gt;&amp;lt;h2&amp;gt;&lt;/code&gt;’s, that means the &lt;code&gt;&amp;lt;h2&amp;gt;&lt;/code&gt;’s found within the content of the page are no longer semantically correct. In this case, they should, in fact, be &lt;code&gt;&amp;lt;h3&amp;gt;&lt;/code&gt;’s. As level three subheadings would provide the correct structure to our content&lt;sup id="fnref:1"&gt;&lt;a href="#fn:1" class="footnote-ref" role="doc-noteref"&gt;1&lt;/a&gt;&lt;/sup&gt;.&lt;/p&gt;
&lt;p&gt;So how do we solve this problem?&lt;/p&gt;
&lt;p&gt;The simple solution is to programatically change the &lt;code&gt;&amp;lt;h2&amp;gt;&lt;/code&gt;’s in our page content to &lt;code&gt;&amp;lt;h3&amp;gt;&lt;/code&gt;’s. But, of course, only in our list template. We don’t want to change them in the actual markdown file, as this would skew the semantics on the single page.&lt;/p&gt;
&lt;h2 id="the-solution"&gt;The solution&lt;/h2&gt;
&lt;p&gt;I must admit, I’m no expert at creating regular expressions. However, Hugo has a few helpful Regex functions that even a novice like me can get some use out of.&lt;/p&gt;
&lt;p&gt;One of these functions is &lt;a href="https://gohugo.io/functions/strings/replacere/" target="_blank" rel="noopener"&gt;strings.ReplaceRE&lt;/a&gt;, which can be used to replace all occurrences of some part of a string (indicated using a regular expression) with a replacement pattern.&lt;/p&gt;
&lt;p&gt;Consider the following updates to our list template.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;define&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;main&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;range&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Pages&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;class&lt;/span&gt;&lt;span class="p"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;flow&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nx"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Title&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;replaceRE&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;h2&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;h3&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;safeHTML&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;&lt;span class="nx"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;You’ll notice we’re now &lt;a href="https://gohugo.io/templates/introduction/#pipes" target="_blank" rel="noopener"&gt;piping&lt;/a&gt; the &lt;code&gt;.Content&lt;/code&gt; method – which is used returns our page content – to the replaceRE function. We pass two arguments to the function, the &lt;em&gt;pattern&lt;/em&gt; we’re looking to replace (in this case &lt;code&gt;h2&lt;/code&gt;) and the value, or &lt;em&gt;replacement&lt;/em&gt;, we want to replace the pattern with (in this case &lt;code&gt;h3&lt;/code&gt;). Finally, as ReplaceRE converts our content into a string, we need to use the &lt;a href="https://gohugo.io/functions/safe/html/" target="_blank" rel="noopener"&gt;safeHTML&lt;/a&gt; pipe to return our content as HTML, instead of a string.&lt;/p&gt;
&lt;p&gt;With that, the level two subheadings in the content of our pages are now level three subheadings, but only in our list templates. They remain level two subheadings in our content files and on single pages. Exactly what we want!&lt;/p&gt;
&lt;p&gt;Those of you creating anchor tags for your headings, may prefer to use the following – slightly more intimidating – few lines of code to make that happen:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;h2toh3&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="o"&gt;:=&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Content&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;replaceRE&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;h2&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;h3&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;safeHTML&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="nx"&gt;h2toh3&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;replaceRE&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;(&amp;lt;h[2-9] id=\&amp;#34;([^\&amp;#34;]+)\&amp;#34;.+)(&amp;lt;/h[2-9]+&amp;gt;)&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;`${1}&amp;amp;nbsp;&amp;lt;a class=&amp;#34;heading-anchor&amp;#34; href=&amp;#34;#${2}&amp;#34; aria-label=&amp;#34;Anchor&amp;#34;&amp;gt;#&amp;lt;/a&amp;gt; ${3}`&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;|&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;safeHTML&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Here we get into some real head scratching regex. But I’ll leave that for you to try and make sense of.&lt;/p&gt;
&lt;p&gt;For those that are interested, you can find an implementation of what we’ve discussed in this post over on &lt;a href="https://harrycresswell.com/now/"&gt;my /now page&lt;/a&gt;.&lt;/p&gt;
&lt;div class="footnotes" role="doc-endnotes"&gt;
&lt;hr&gt;
&lt;ol&gt;
&lt;li id="fn:1"&gt;
&lt;p&gt;I recommend &lt;a href="https://webaim.org/techniques/headings/#visualonly" target="_blank" rel="noopener"&gt;WebAIM’s article on headings&lt;/a&gt; for further reading on how to correctly structure your content with headings.&amp;#160;&lt;a href="#fnref:1" class="footnote-backref" role="doc-backlink"&gt;&amp;#x21a9;&amp;#xfe0e;&lt;/a&gt;&lt;/p&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;/div&gt;

&lt;p&gt;It was interesting to read about &lt;a href="https://rknight.me/blog/on-transient-slash-pages/" target="_blank" rel="noopener"&gt;the evolution of Robb’s /now page&lt;/a&gt;. How he’s moving away from a transient, impersonal page of data towards something more meaningful.&lt;/p&gt;
&lt;p&gt;On a related note, I completely agree with &lt;a href="https://chriscoyier.net/2025/01/04/slash-pages/" target="_blank" rel="noopener"&gt;Chris’ thoughts on slash pages&lt;/a&gt;. I found myself nodding along in agreement, to these few lines in particular:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;“I think it makes more sense to me to write about what I carry around &lt;em&gt;right now&lt;/em&gt; and if I did it again later, it doesn’t have to replace what I’ve written before. It would be interesting and worth preserving each time.”&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;On &lt;a href="https://shoptalkshow.com/647/" target="_blank" rel="noopener"&gt;ShopTalkShow episode 647&lt;/a&gt;, Chris shares another concern he has with slash pages. How does anyone ever see them if they aren’t syndicated (for example via RSS)? It’s true, they’d be far more visible as blog posts.&lt;sup id="fnref:1"&gt;&lt;a href="#fn:1" class="footnote-ref" role="doc-noteref"&gt;1&lt;/a&gt;&lt;/sup&gt;&lt;/p&gt;
&lt;p&gt;I’ve been keeping &lt;a href="https://harrycresswell.com/now/"&gt;my own /now page&lt;/a&gt; since 2017. I’ve made countless updates to it since then. Now I think about it, it’s a real shame there’s no record of what I wrote previously. If you had nothing better to do and were really that interested, then I suppose you could trawl through my Github commits. But who in their right mind could be bothered to do that?&lt;/p&gt;
&lt;p&gt;This brings me to &lt;a href="https://www.zinzy.website/now/" target="_blank" rel="noopener"&gt;Zinzy’s /now page&lt;/a&gt;, which I’m a big fan of. What I like most about it is that it feels as if you’re getting a quick glimpse into what’s happening in Zinzy’s life, at that particular moment. I like how each update is organised by date and how previous updates are preserved as an archive - just like how a regular blog works.&lt;/p&gt;
&lt;p&gt;It would be fun to try something similar with my own /now page. Make it feel more like a bunch of quick journal entries. This approach definitely makes the whole now page thing way more personal and interesting.&lt;/p&gt;
&lt;p&gt;In my head, I picture myself posting these updates from a chairlift in the Alps. Or whilst out on a hike with friends. Perhaps I’m overestimating how interesting my day-to-day life actually is. I suppose we’ll find out soon enough. Whatever the case, with the demise of Big Social, it feels like the right sort of time to make this happen.&lt;/p&gt;
&lt;p&gt;I expect the key to making it work is figuring out a simple system that makes posting these “now” updates as frictionless as possible. Realistically, that’s the only way I’m likely to stick with it, but I guarantee that’s easier said than done.&lt;/p&gt;
&lt;p&gt;Before I get too deep in the weeds with that, the first step is to change how my /now page functions. Make it act more like a blog which lists recent updates, and less like a boring transient page which rarely ever gets updated.&lt;/p&gt;
&lt;div class="footnotes" role="doc-endnotes"&gt;
&lt;hr&gt;
&lt;ol&gt;
&lt;li id="fn:1"&gt;
&lt;p&gt;&lt;a href="https://notes.neatnik.net/2025/01/everything-is-a-web-page" target="_blank" rel="noopener"&gt;Adam&lt;/a&gt; and &lt;a href="https://coryd.dev/posts/2025/most-pages-are-actually-posts/" target="_blank" rel="noopener"&gt;Cory&lt;/a&gt; also have some interesting thoughts on which pages to syndicate.&amp;#160;&lt;a href="#fnref:1" class="footnote-backref" role="doc-backlink"&gt;&amp;#x21a9;&amp;#xfe0e;&lt;/a&gt;&lt;/p&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;/div&gt;

&lt;p&gt;Trying to get into the whole food prep thing. The goal is to always have healthy options to pull out of the fridge throughout the week. I tend make unhealthy decisions when I’m short on time and there’s nothing convenient to eat, so preparing a few versatile things helps to solve this problem.&lt;/p&gt;
&lt;p&gt;For the last couple of weeks we’ve made a bunch of spreads and dips on a Sunday afternoon. Usually a humous, a romesco and some sort of pesto that will go well with anything, from veggie sticks to pasta. We also roast some cashews and almonds and make cacao-based energy balls to replace unhealthy sweet snacks, which I often crave towards the end of the day.&lt;/p&gt;
&lt;p&gt;Recently I learnt how important it is to &lt;a href="https://youtu.be/3esF-pNAM9c?si=cOlIV89BCnkhbja0&amp;amp;t=1771" target="_blank" rel="noopener"&gt;start the day with a savoury meal&lt;/a&gt; (spoiler: it helps to reduce your blood glucose spikes which can lead to all sorts of disease). So yesterday we prepared all the ingredients for breakfast burritos with a bit of a healthy twist – scrambled tofu, homemade baked beans, guacamole, kimchi and spinach and chickpea flour pancakes.&lt;/p&gt;
&lt;p&gt;I have to say, it felt kinda weird eating a burrito for breakfast on a Monday, And part of me missed the usual porridge topped with seeds, fruit and nuts. But it was tasty nonetheless and I’m sure I can get into it.&lt;/p&gt;
&lt;p&gt;Hopefully we can keep this up, but let’s see how it goes. At some point I’d like to get another glucose monitor to see if this new approach to breakfast actually makes a difference.&lt;/p&gt;

&lt;p&gt;Thought I’d give the Blog Questions Challenge a go, having seen &lt;a href="https://kevquirk.com/blog/blog-questions-challenge" target="_blank" rel="noopener"&gt;Kev&lt;/a&gt;, &lt;a href="https://www.lkhrs.com/blog/2025/blog-questions-challenge/" target="_blank" rel="noopener"&gt;Luke&lt;/a&gt; and &lt;a href="https://manuelmoreale.com/blog-questions-challenge" target="_blank" rel="noopener"&gt;Manu&lt;/a&gt; share theirs.&lt;/p&gt;
&lt;p&gt;I’m also not on Bear Blog, so I’m using the questions Kev adapted from &lt;a href="https://blog.avas.space/bear-blog-challenge/" target="_blank" rel="noopener"&gt;Ava’s original ones&lt;/a&gt; for my own.&lt;/p&gt;
&lt;h2 id="why-did-you-start-blogging-in-the-first-place"&gt;Why did you start blogging in the first place?&lt;/h2&gt;
&lt;p&gt;Around 2017, I started taking notes on the design and web dev stuff I was learning. Mostly so I had something to refer back to when I forgot what I’d learnt. Around the same time, I read Shawn’s post &lt;a href="https://www.swyx.io/learn-in-public" target="_blank" rel="noopener"&gt;Learn In Public&lt;/a&gt; or one similar, I can’t quite remember. Following that, it seemed sensible to publish these notes as posts on my site.&lt;/p&gt;
&lt;p&gt;Through the process I discovered that I quite enjoyed writing – how it helps you slow down and deepen your understanding of the things you’re writing about – so I kept it going. I realised this doesn’t just apply to technical posts, but also writing about life in general. That’s when I started posting about various other topics.&lt;/p&gt;
&lt;h2 id="what-platform-are-you-using-to-manage-your-blog-and-why-did-you-choose-it"&gt;What platform are you using to manage your blog and why did you choose it?&lt;/h2&gt;
&lt;p&gt;&lt;a href="https://gohugo.io/" target="_blank" rel="noopener"&gt;Hugo&lt;/a&gt; hosted on &lt;a href="https://www.netlify.com/" target="_blank" rel="noopener"&gt;Netlify&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;I chose Hugo because I wanted to use a simple, open-source tool, that required minimal maintenance and that I could stick with long-term.&lt;/p&gt;
&lt;p&gt;What I love about Hugo is that it’s pretty much just Markdown files and a few basic templates. It doesn’t get much simpler than that. You don’t need any extra dependencies to run a Hugo site, so they can be super easy to maintain. I’ve used Hugo for the past 8 years and have no plans on switching anytime soon.&lt;/p&gt;
&lt;p&gt;I chose Netlify because it was one of the few options I knew of at the time, that could deploy a site from commits pushed to a Github repository. It was also super easy to set up and cost nothing. There may well be a bunch of better options these days, but I haven’t had any issues yet, so I’ve stuck with it.&lt;/p&gt;
&lt;h2 id="have-you-blogged-on-other-platforms-before"&gt;Have you blogged on other platforms before?&lt;/h2&gt;
&lt;p&gt;Way back when I took more photos I used a bunch of different tools, like &lt;a href="https://www.flickr.com/" target="_blank" rel="noopener"&gt;flickr&lt;/a&gt;, &lt;a href="https://www.tumblr.com/" target="_blank" rel="noopener"&gt;Tumblr&lt;/a&gt;, &lt;a href="https://www.koken.me/" target="_blank" rel="noopener"&gt;Koken&lt;/a&gt; and &lt;a href="https://wordpress.org/" target="_blank" rel="noopener"&gt;Wordpress&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;I had a brief spell using Medium, but since I’ve been writing I’ve mostly used Hugo.&lt;/p&gt;
&lt;h2 id="how-do-you-write-your-posts-for-example-in-a-local-editing-tool-or-in-a-paneldashboard-thats-part-of-your-blog"&gt;How do you write your posts? For example, in a local editing tool, or in a panel/dashboard that&amp;rsquo;s part of your blog?&lt;/h2&gt;
&lt;p&gt;These days all my posts start life in &lt;a href="https://obsidian.md/" target="_blank" rel="noopener"&gt;Obsidian&lt;/a&gt;. I create a new note, then add my post template. The template adds some front matter to help organise the note, when I’m done with it later.&lt;/p&gt;
&lt;p&gt;For more general posts, I tend to just hammer out some words. For technical posts, I usually get a rough outline down first, then expand on that.&lt;/p&gt;
&lt;p&gt;Most of this happens at my desk, but Obsidian is synced with my other devices via iCloud, so I can also work on posts from my iPhone when I’m on the move.&lt;/p&gt;
&lt;p&gt;Once I have a rough draft, I’ll read through it, make edits and check for any obvious mistakes. Once I’m happy with it, I open the local copy of my site using &lt;a href="https://code.visualstudio.com/" target="_blank" rel="noopener"&gt;VS Code&lt;/a&gt;. Then I create a new markdown file and copy and paste the markdown from Obsidian into the new file. Next I run the local server and read through it in a browser.&lt;/p&gt;
&lt;p&gt;When I look at a post in a browser, I usually spot a bunch more ways to improve it. I make these changes in VS Code.&lt;/p&gt;
&lt;h2 id="when-do-you-feel-most-inspired-to-write"&gt;When do you feel most inspired to write?&lt;/h2&gt;
&lt;p&gt;Usually in the mornings, when I have a fresh head.&lt;/p&gt;
&lt;p&gt;Often I’ll think of something I want to write about when I’m out for a run or in the shower. Or when I find a post in &lt;a href="https://netnewswire.com/" target="_blank" rel="noopener"&gt;my RSS reader&lt;/a&gt; and I feel like I have something to say about it. These posts often end up on &lt;a href="https://harrycresswell.com/links/"&gt;my links page&lt;/a&gt;.&lt;/p&gt;
&lt;h2 id="do-you-publish-immediately-after-writing-or-do-you-let-it-simmer-a-bit-as-a-draft"&gt;Do you publish immediately after writing, or do you let it simmer a bit as a draft?&lt;/h2&gt;
&lt;p&gt;I tend to publish the day after I’ve written something. I like to read through a post the following morning, with a fresh pair of eyes. Then make some final edits, before pushing it live.&lt;/p&gt;
&lt;p&gt;I often go back to posts I’ve already published. Usually the day after. I always seem to find a ton of ways to improve a post, as I read through the live version.&lt;/p&gt;
&lt;h2 id="whats-your-favourite-post-on-your-blog"&gt;What&amp;rsquo;s your favourite post on your blog?&lt;/h2&gt;
&lt;p&gt;&lt;a href="https://harrycresswell.com/writing/menus-in-hugo/"&gt;Menus in Hugo&lt;/a&gt;, because it’s the one most people have contacted me about or thanked me for.&lt;/p&gt;
&lt;p&gt;Or &lt;a href="https://harrycresswell.com/writing/okr-in-obsidian/"&gt;OKR in Obsidian&lt;/a&gt;, because it’s the one that’s had the biggest impact on how I work.&lt;/p&gt;
&lt;h2 id="any-future-plans-for-your-blog-maybe-a-redesign-a-move-to-another-platform-or-adding-a-new-feature"&gt;Any future plans for your blog? Maybe a redesign, a move to another platform, or adding a new feature?&lt;/h2&gt;
&lt;p&gt;No plans to switch platforms or take on a full-blown redesign. I mostly just chip away at it and try to make small incremental improvements.&lt;/p&gt;
&lt;p&gt;I do have a few things on my todo list though:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;Adding an about page&lt;/li&gt;
&lt;li&gt;Adding a bookmarks page&lt;/li&gt;
&lt;li&gt;Adding a photos section&lt;/li&gt;
&lt;li&gt;Adding a graveyard for dead projects&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;I tend to move at a sloth-like pace, but hopefully I can tackle these things in 2025.&lt;/p&gt;

&lt;p&gt;Mornings are generally dark and gloomy, here in the UK, at this time of year. It’s never easy getting up and out for a run. In recent years, I’ve become somewhat of a fair-weather runner. If there’s any chance of rain, I tend to sack it off altogether, roll out my mat and do some yoga instead.&lt;/p&gt;
&lt;p&gt;This morning, rain seemed inevitable. Overcast, low dark clouds, all the usual signs. It was only a matter of time before a celestial downpour. For whatever reason I decided to brave it anyway. Perhaps the start of a new year has given me the extra motivation I needed.&lt;/p&gt;
&lt;p&gt;Within a few minutes of being out there, the wind picked up and, to no surprise, it began to rain. Soon enough it was hammering it down. Thirty seconds later I was completely sodden. I usually feel miserable when I’m caught in the rain and soaked to the bone, but today I decided to embrace it instead.&lt;/p&gt;
&lt;p&gt;Not sure exactly why, but I began to imagine what it must be like coming from a place where rain rarely ever falls. How would it feel if this was the first rain you’d felt in years? Or, maybe even your first ever experience of the stuff?&lt;/p&gt;
&lt;p&gt;In a word, it must feel &lt;em&gt;incredible&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;I spent the final part of my run with this in mind. Trying to appreciate the cold, wet, wind and rain, beating my body and face. Out there in the elements, totally drenched, it felt amazing.&lt;/p&gt;
&lt;p&gt;I stopped avoiding puddles as I made my way home. In these sorts of conditions, it makes little difference, so I splashed right through them. It felt just like being a kid again. Outside in the rain, jumping in puddles, having the best of times.&lt;/p&gt;
&lt;p&gt;I came through the front door, dripping wet, with the biggest smile on my face.&lt;/p&gt;
&lt;p&gt;We’re fortunate to have “bad” weather like this in abundance here in the UK. It’s worth trying to appreciate it more.&lt;/p&gt;

&lt;p&gt;With another year almost over, I’ve spent these final few days looking back at the past 12 months. Here are some of those reflections in writing - what went well, what didn’t go so well and what I learnt in the year 2024.&lt;/p&gt;
&lt;p&gt;For those interested in how I approach these end of year reflections, check out &lt;a href="https://harrycresswell.com/writing/year-in-review/"&gt;a year in review&lt;/a&gt;. Perhaps this will give you some inspiration to start writing your own, if you don’t already.&lt;/p&gt;
&lt;p&gt;With that said, let’s get right to it.&lt;/p&gt;
&lt;h2 id="what-went-well"&gt;What went well?&lt;/h2&gt;
&lt;h3 id="general"&gt;General&lt;/h3&gt;
&lt;p&gt;It’s been just over a year since I moved into a new house. After initially struggling with the idea of moving, I now feel this is one of the best decisions I’ve ever made. I love the house and couldn’t be happier in this leafy part of south-east London. It’s been slow progress with the renovation work, but I’m super happy with the improvements we’ve made and I’m excited to continue the work in the new year.&lt;/p&gt;
&lt;p&gt;Now we have a house, we also have a bit more space, including an office room, which has been a total game changer considering we used to work together in our one-bedroom flat. More space means we’ve been able to have a bunch of friends and family staying throughout the year. It’s nice to be in a position to offer our home to others. We’ve also hosted a handful of dinners, which have been really fun.&lt;/p&gt;
&lt;p&gt;In a similar vein, Rebekka and I hosted a big party for her 30th birthday in October. I’ve always shied away from the idea of celebrating birthdays in a big way, but I’ve really come round to the idea. I realise it’s important to celebrate things. Whether it’s a birthday or just something that’s going well in your life. It’s also a great excuse to get friends and family together in the same room, which doesn’t happen often enough.&lt;/p&gt;
&lt;p&gt;On another note, it’s been a great year for Chelsea FC fans. After a change in ownership, a complete rebuild and a difficult couple of seasons, it seems like we’re finally back on track. This is my 27th (I think) season going to Chelsea games with my dad. I’m proud of that commitment. Supporting a team and watching football is a strangely addictive thing. We’ve often contemplated the point of it all, but we keep coming back because there’s nothing quite like it. The shared highs and lows, celebrating with other fans, the feeling of belonging to a community. I’ve sat with the same group for the most part of my life - many I now consider friends. The main thing we have in common is our continued commitment to a football club. How bizarre. I laugh with my dad that it’s too late to give up now.&lt;/p&gt;
&lt;p&gt;My streak on Duolingo is almost at 1 whole year. I’m proud of that achievement. I’ve struggled to practice German consistently in previous years, so this is a huge step in the right direction. I’ve toyed with the idea of getting private lessons, but that hasn’t happened yet. Perhaps I’ll look into this again in the new year. But, as someone who snail paces through life, I’m in no particular rush.&lt;/p&gt;
&lt;p&gt;I always had a book on the go this year and I’ve read some interesting stuff.
Some fiction, some non-fiction. Books about finance, physics and economics. Some esoteric. Some eye-opening. Some just really entertaining. I like to keep an open mind and, as such, I’m open to reading all sorts of things. However functional and pragmatic, or wacky and out there, the subject matter may seem.&lt;/p&gt;
&lt;h3 id="work"&gt;Work&lt;/h3&gt;
&lt;p&gt;This year has been my most successful year freelancing since setting up my business. A big part of that I owe to the help I’ve had from Rebekka and Ben. Right now I have a good mix of regular clients and a handful of projects already lined up 2025. There’s much still to do, such as launching &lt;a href="https://studioeryn.com/" target="_blank" rel="noopener"&gt;my studio website&lt;/a&gt;. But things are heading in the direction I want them to, which is, without doubt, the most important thing.&lt;/p&gt;
&lt;p&gt;Things haven’t been too bad for personal projects either. In January &lt;a href="https://harrycresswell.com/writing/introducing-cu-css/"&gt;I launched cu.css&lt;/a&gt;, the CSS starter I use for both client and personal projects. In recent years, I’ve been working hard to formalise my approach to designing and coding websites. &lt;em&gt;cu.css&lt;/em&gt; has been a big part of this journey. Methodologies like CUBE CSS and Utopia – which &lt;em&gt;cu.css&lt;/em&gt; is heavily based on – have not only helped me create leaner, more performant websites, but have also helped to remove the uncertainty around the way I want to be doing things. The result of which is becoming a more efficient and productive web developer.&lt;/p&gt;
&lt;p&gt;I’m also very close to launching the first season of &lt;a href="https://practicalhugo.com/" target="_blank" rel="noopener"&gt;Practical Hugo&lt;/a&gt;. Since I’m not in a position to commit 100% of my time to it, this one has been a long time coming. Thanks to all of you who have been waiting patiently since signing up for the pre-release. I hope it will be worth the wait and a useful resource for the Hugo community.&lt;/p&gt;
&lt;h3 id="travel"&gt;Travel&lt;/h3&gt;
&lt;p&gt;In March I organised a ski trip to La Plagne in France for group of 10. I’d never organised a group trip like this before so I was kinda nervous about how it was going to pan out. Thankfully it couldn’t have gone any smoother. Everyone had a blast, including the beginners, most of whom were on skis for the first time. We’re going for round 2 in 2025, so that’s gotta be a good sign.&lt;/p&gt;
&lt;p&gt;I was in Germany on three separate occasions this year. At the end of March we were in Bremen, then in September and December back in Hamburg. I’m very fond of Hamburg, having lived there in the past. So it was fun to spend time there with my family and check out restaurants and sights I hadn’t been to or seen before.&lt;/p&gt;
&lt;p&gt;In September I took 2 weeks off to travel around Croatia, where Rebekka and I first met, almost 10 years ago. We spent time in Split, Brač, Korčula and Dubrovnik. Croatia is a beautiful place. I‘ll try not to leave it so long before I visit again.&lt;/p&gt;
&lt;p&gt;The rest of my year was mostly spent at home in London. However we did manage a few trips to various other parts of the UK: Rye, Wiltshire, Warwickshire, St Leonards and Dorset.&lt;/p&gt;
&lt;h3 id="health"&gt;Health&lt;/h3&gt;
&lt;p&gt;I’m struggling to recall being sick at all this year, except for a cold in January. For most the year I’ve felt really good. I’ve made sure to rest when I’ve not felt my best and always tried to get plenty of sleep. I’ve also exercised consistently and been mindful about what I put in my body – trying to eat a variety of healthy food often. A while back, &lt;a href="https://www.netflix.com/tudum/articles/stutz-the-tools" target="_blank" rel="noopener"&gt;Phil Stutz introduced me to the Life Force model&lt;/a&gt;, so I try to prioritise work on my physical body (sleep, nutrition and exercise) as much as possible.&lt;/p&gt;
&lt;p&gt;It’s been a good year for running. I went for my first Parkrun on Christmas Day last year and subsequently went for a bunch more throughout the year. In May I ran the Hackney Half Marathon for the first time. I’m not sure I’ve ever had so much fun running. I have another half coming up next year, this time in Hamburg, which I’m excited about. Signing up for a race is a great way to find some focus with your training. I’d like to do more interval training in 2025 and try to improve my 5K time.&lt;/p&gt;
&lt;p&gt;I‘ve had another consistent year of yoga. R and I go to a classes 2-3 times per week. Since my favourite teacher took a year out, we’ve branched out to trying new studios and new teachers. It’s been fun to explore London more and experience other teaching styles. Hopefully we can continue this adventure in the new year and perhaps do a little more strength training. Everyone (particularly my PT sister) keeps telling me it’s important, especially as we grow older.&lt;/p&gt;
&lt;p&gt;I went bouldering with friends a bunch of times this year. I’d like to go more as it’s such a great way to catch up whilst doing something that’s good for your body. It’s just so damn expensive in London these days.&lt;/p&gt;
&lt;h2 id="what-didnt-go-so-well"&gt;What didn’t go so well?&lt;/h2&gt;
&lt;p&gt;It wasn&amp;rsquo;t a great year for publishing blog posts. I tend to start the year well, then hit a wall towards the middle of the year (typically when I’m really busy with work). I struggled to find a balance between writing course content and writing blog posts. I want to make a real effort to commit, at least, an hour each day to writing. Even when work, and life in general, gets busy.&lt;/p&gt;
&lt;p&gt;I didn’t complete any courses this year. I dipped in and out of &lt;a href="https://visualizevalue.com/products/compound-content" target="_blank" rel="noopener"&gt;Compound Content&lt;/a&gt; from Visualise Value, but I didn’t finish it. I want to change this in the new year. I picked up &lt;a href="https://piccalil.li/complete-css/" target="_blank" rel="noopener"&gt;Complete CSS&lt;/a&gt; and &lt;a href="https://radicaldesigncourse.com/" target="_blank" rel="noopener"&gt;Radical Design&lt;/a&gt; during the Black Friday sale. So if I can complete Compound Content and see these two through before this time next year, then I’ll be happy and feel like I’m not stagnating.&lt;/p&gt;
&lt;p&gt;I’ve made a considerable effort with digital decluttering in recent years, but I didn’t make a huge amount of progress this year. I’m still working through my old Notion bookmarks, but it always gets relegated down the todo list in favour of other more pressing tasks. I also need to add YouTube and Spotify to my todo list, as I realise I’ve created a real mess with playlists. I probably need to think more about my system and perhaps be more strict with myself when I comes to sticking with my time blocks.&lt;/p&gt;
&lt;p&gt;I had big plans to ski in Milton Keynes in preparation for this ski years season, but I failed miserably. Perhaps lugging skis across London is just too much effort to do regularly. With a group of friends who are now also keen to ski more regularly, perhaps we‘ll be able to hold each other accountable in 2025. We’ll see.&lt;/p&gt;
&lt;h2 id="what-did-i-learn"&gt;What did I learn?&lt;/h2&gt;
&lt;p&gt;In no particular order, here are a few things I learned this year:&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;Organising events is fun.&lt;/li&gt;
&lt;li&gt;Push yourself outside of your comfort zone, you might even enjoy it.&lt;/li&gt;
&lt;li&gt;The right decision is usually found within, not by following what works for others.&lt;/li&gt;
&lt;li&gt;Let your values dictate your actions.&lt;/li&gt;
&lt;li&gt;Be generous with your time and knowledge.&lt;/li&gt;
&lt;li&gt;Play long term games. The things you stick with the longest are typically the most rewarding. Relationships, side-projects, investing, interests and hobbies all apply here.&lt;/li&gt;
&lt;li&gt;Share your space, offer it to those in need.&lt;/li&gt;
&lt;li&gt;Reality is the product of relationships and interactions, without which there is nothing.&lt;/li&gt;
&lt;li&gt;Rich is what you see (income, fast cars, etc). Wealth is what you don’t see (years of boring saving and investing).&lt;/li&gt;
&lt;li&gt;Everything is paranormal until we understand it, at which point it becomes normal.&lt;/li&gt;
&lt;li&gt;Consider all life experiences, even the ones that seem implausible or difficult to comprehend.&lt;/li&gt;
&lt;/ul&gt;
&lt;h2 id="final-words"&gt;Final words&lt;/h2&gt;
&lt;p&gt;All in all, 2024 has been a positive year and I’m happy with how it turned out.&lt;/p&gt;
&lt;p&gt;I worked hard, but also took it at my own pace. I’ve tried to manage my expectations and let go of the mindset that I should always be achieving more. As a result, it’s been the first year in a while where I never felt too stressed.&lt;/p&gt;
&lt;p&gt;I now realise how important it is to appreciate where you’re at in life, rather than constantly striving for more. When life it going well, acknowledge (or celebrate) it. It will help you realise that you’re doing ok.&lt;/p&gt;
&lt;p&gt;Writing these reflections continues to be a great way to spot progress, however slow or insignificant it sometimes feels. Looking back over past years is also a great way to benchmark progress against yourself, instead of comparing yourself others.&lt;/p&gt;
&lt;p&gt;As in previous years, it’s the sum of the things I’ve stuck with the most, and new things I’ve pushed myself to try, that turned out to be the most rewarding.&lt;/p&gt;
&lt;p&gt;Anyway, hopefully this has be as helpful to read as it has been for me to write. And, I wish you all a healthy and prosperous start to the year 2025.&lt;/p&gt;

&lt;p&gt;As part of the Hugo v0.118.0 release, the Hugo team made some improvements to both the &lt;code&gt;hugo new site&lt;/code&gt; and &lt;code&gt;hugo new theme&lt;/code&gt; commands.&lt;/p&gt;
&lt;p&gt;The big change, as &lt;a href="https://discourse.gohugo.io/t/creating-new-site-and-theme-skeletons-with-v0-118-0-and-later/46018" target="_blank" rel="noopener"&gt;Joe has pointed out&lt;/a&gt;, is that &lt;code&gt;hugo new theme&lt;/code&gt; command now creates a functional theme with sample content. The new theme demonstrates some of the typical features of Hugo, so it’s a great way to learn how Hugo works and a helpful starting point for theme development.&lt;/p&gt;
&lt;p&gt;In this post I run through these improvements and explain some of the more advanced features that you’ll find in the new starter theme. But, before we get to that, first let’s take a brief look at the updates to the &lt;code&gt;hugo new site&lt;/code&gt; command.&lt;/p&gt;
&lt;h2 id="running-the-hugo-new-site-command"&gt;Running the &lt;code&gt;hugo new site&lt;/code&gt; command&lt;/h2&gt;
&lt;p&gt;Running &lt;code&gt;hugo new site [site-name]&lt;/code&gt; from the command line will set up a new site, as expected, but with a handful of notable improvements.&lt;/p&gt;
&lt;p&gt;Firstly, the documentation returned to the terminal has been improved with clearer steps and more emphasis on creating a new theme.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;Congratulations! Your new Hugo site was created in /Users/harrycresswell/code/hugo/hugo-new.
Just a few more steps...
1. Change the current directory to /Users/harrycresswell/code/hugo/hugo-new.
2. Create or install a theme:
- Create a new theme with the command &amp;#34;hugo new theme &amp;lt;THEMENAME&amp;gt;&amp;#34;
- Or, install a theme from https://themes.gohugo.io/
3. Edit hugo.toml, setting the &amp;#34;theme&amp;#34; property to the theme name.
4. Create new content with the command &amp;#34;hugo new content &amp;lt;SECTIONNAME&amp;gt;/&amp;lt;FILENAME&amp;gt;.&amp;lt;FORMAT&amp;gt;&amp;#34;.
5. Start the embedded web server with the command &amp;#34;hugo server --buildDrafts&amp;#34;.
See documentation at https://gohugo.io/.
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Open the site skeleton that Hugo generates and you’ll notice the directory structure is similar to before. All the project files and folders you’ll be familiar with remain, however there are two updates.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;- archetypes
- default.md
- assets
- content
- data
- i18n
- layouts
- static
- themes
- hugo.toml
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Hugo now includes the &lt;code&gt;/i18n&lt;/code&gt; folder, which is used to store &lt;a href="https://gohugo.io/functions/lang/translate/#translation-tables" target="_blank" rel="noopener"&gt;translation tables&lt;/a&gt;. You’ll only find a need to use this folder when working with &lt;a href="https://gohugo.io/content-management/multilingual/" target="_blank" rel="noopener"&gt;multilingual mode&lt;/a&gt;, for sites that offer more than one language.&lt;/p&gt;
&lt;p&gt;In case you were wondering, &lt;strong&gt;i18n&lt;/strong&gt; is a &lt;a href="https://en.wikipedia.org/wiki/Numeronym" target="_blank" rel="noopener"&gt;numeronym&lt;/a&gt; which stands for &lt;a href="https://en.wikipedia.org/wiki/Internationalization_and_localization" target="_blank" rel="noopener"&gt;internationalization&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;archetypes/default.md&lt;/code&gt; file has also changed slightly. The value for the front matter &lt;em&gt;title&lt;/em&gt; is now generated using &lt;code&gt;.File.ContentBaseName&lt;/code&gt; instead of &lt;code&gt;.Name&lt;/code&gt;, which was used previously.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;+++
title = &amp;#39;{{ replace .File.ContentBaseName &amp;#34;-&amp;#34; &amp;#34; &amp;#34; | title }}&amp;#39;
date = {{ .Date }}
draft = true
+++
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Reading the docs, it seems &lt;a href="https://gohugo.io/methods/page/file/#contentbasename" target="_blank" rel="noopener"&gt;ContentBaseName&lt;/a&gt; will generated the title of the content from the name of the containing directory. That is, if the page is a &lt;a href="https://gohugo.io/content-management/page-bundles/#branch-bundles" target="_blank" rel="noopener"&gt;branch bundle&lt;/a&gt; (/some-directory/_index.md) or a &lt;a href="https://gohugo.io/content-management/page-bundles/#leaf-bundles" target="_blank" rel="noopener"&gt;leaf bundle&lt;/a&gt; (/some-directory/index.md). See &lt;a href="https://gohugo.io/content-management/page-bundles/" target="_blank" rel="noopener"&gt;page bundles&lt;/a&gt; for more. If the page is neither, the title will be generated using &lt;a href="https://gohugo.io/methods/page/file/#translationbasename" target="_blank" rel="noopener"&gt;TranslationBaseName&lt;/a&gt;, which takes the file name, excluding the extension and language identifier.&lt;/p&gt;
&lt;p&gt;The rest of the files and folders either remain empty or as they were before the v0.118.0 release.&lt;/p&gt;
&lt;p&gt;With the updates to &lt;code&gt;hugo new site&lt;/code&gt; covered, let’s now take a look at the &lt;code&gt;hugo new theme&lt;/code&gt; command.&lt;/p&gt;
&lt;h2 id="running-the-hugo-new-theme-command-pre-hugo-v01180-upgrade"&gt;Running the &lt;code&gt;hugo new theme&lt;/code&gt; command pre Hugo v0.118.0 upgrade&lt;/h2&gt;
&lt;p&gt;Until Hugo v0.118.0, running &lt;code&gt;hugo new theme [theme-name]&lt;/code&gt; from the command line would set up a new theme directory inside &lt;code&gt;/themes&lt;/code&gt;, consisting of a basic theme structure and mostly empty files.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;my-site/
- archetypes/
- default.md
- layouts/
- _default/
- baseof.html
- list.html
- single.html
- partials/
- footer.html
- head.html
- header.html
404.html
index.html
- static/
- css/
- js/
- LICENCE
- theme.toml
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;There were, however, two files that weren’t empty. The &lt;code&gt;_default/baseof.html_&lt;/code&gt; file, which demonstrated how to include partial templates and register a block. And the &lt;code&gt;theme.toml&lt;/code&gt; file, which offered an example theme file configuration.&lt;/p&gt;
&lt;p&gt;We won’t bother exploring these legacy files, instead let’s jump ahead to the improvements.&lt;/p&gt;
&lt;h2 id="running-the-hugo-new-theme-command-with-hugo-v01180-or-later"&gt;Running the &lt;code&gt;hugo new theme&lt;/code&gt; command with Hugo v0.118.0 or later&lt;/h2&gt;
&lt;p&gt;Following an upgrade to Hugo v0.118.0 or later, running &lt;code&gt;hugo new theme [theme-name]&lt;/code&gt; now sets up a fully functional theme with sample content.&lt;/p&gt;
&lt;p&gt;Here’s the new theme directory structure:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;my-theme/
- archetypes/
- default.md
- assets/
- css/
- main.css
- js/
- main.js
- content/
- posts/
- post-3/
- bryce-canon.jpg
- index.md
_index.md
post-1.md
post-2.md
_index.md
- data/
- i18n/
- layouts/
- _default/
- baseof.html
- home.html
- list.html
- single.html
- partials/
- head/
- css.html
- js.html
- footer.html
- head.html
- header.html
- menu.html
- terms.html
- static/
- favicon.ico
- hugo.toml
- LICENCE
- README.md
- theme.toml
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;The two parts of the new theme I want to focus on most are content structure and layout templates, however before we get into that let’s quickly run through the directory structure, so we know what’s what.&lt;/p&gt;
&lt;h2 id="understanding-the-directory-structure"&gt;Understanding the directory structure&lt;/h2&gt;
&lt;p&gt;The &lt;code&gt;/archetypes&lt;/code&gt; folder, used for content templates, now contains a working &lt;em&gt;default.md&lt;/em&gt; archetype template, instead of an empty &lt;em&gt;default.md&lt;/em&gt; file, as before. This archetype template is no different from the template Hugo generates when running the &lt;code&gt;hugo new site&lt;/code&gt; command and will generate the front matter for any file created using &lt;code&gt;hugo new content [name]&lt;/code&gt;, however this one is obviously specific to the theme, whereas the one at the root of the project isn’t.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;/assets&lt;/code&gt; folder, which is used store files to be processed with Hugo Pipes, now contains two sub folders – &lt;em&gt;css&lt;/em&gt; and &lt;em&gt;js&lt;/em&gt;. The &lt;em&gt;css&lt;/em&gt; folder contains a &lt;code&gt;main.css&lt;/code&gt; file containing basic styles and the &lt;em&gt;js&lt;/em&gt; folder a &lt;code&gt;main.js&lt;/code&gt; file, which simply logs &lt;em&gt;“This site was generated by Hugo”&lt;/em&gt; to the console. We’ll get to how the theme consumes these files shortly.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;/content&lt;/code&gt;folder, which is used to store page content, contains a various content files and folders, which we’ll look at in detail later.&lt;/p&gt;
&lt;p&gt;Both the &lt;code&gt;/data&lt;/code&gt; folder – used to store files of structured data much like a conventional database – and the &lt;code&gt;i18n&lt;/code&gt; folder – used to store translation tables for multilingual mode sites (as discussed earlier) – are empty, so we’ll skip over these.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;/layouts&lt;/code&gt; folder contains a &lt;em&gt;_default&lt;/em&gt; folder, which stores bunch of &lt;em&gt;default&lt;/em&gt; layout templates. It also contains a &lt;em&gt;partials&lt;/em&gt; folder, which stores partial layout templates. We’ll look at these templates in more detail shortly.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;/static&lt;/code&gt; folder – used to store any static asset that don’t require processing by Hugo – contains a &lt;code&gt;favicon.ico&lt;/code&gt; file, which creates a favicon visible in browser tabs, usually to the left of the site name.&lt;/p&gt;
&lt;p&gt;&lt;code&gt;hugo.toml&lt;/code&gt; contains the theme configuration, including the menu item which we’ll also look at later.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;LICENSE&lt;/code&gt; file contains the standard MIT License template.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;README.md&lt;/code&gt; file contains a few headings to help you get started writing a readme.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;theme.toml&lt;/code&gt; file contains information about the theme.&lt;/p&gt;
&lt;p&gt;Ok, let’s tackle the content folder.&lt;/p&gt;
&lt;h2 id="content-structure-and-page-bundles"&gt;Content structure and page bundles&lt;/h2&gt;
&lt;p&gt;Inside the &lt;code&gt;/content&lt;/code&gt; folder we find an &lt;code&gt;_index.md&lt;/code&gt; file at the root. This file supplies this content to the &lt;a href="https://gohugo.io/templates/homepage/" target="_blank" rel="noopener"&gt;homepage template&lt;/a&gt; found at &lt;code&gt;layouts/_default/home.html&lt;/code&gt;. You’ll see from the &lt;a href="https://gohugo.io/templates/lookup-order/#home-page" target="_blank" rel="noopener"&gt;homepage lookup order&lt;/a&gt; that &lt;code&gt;home.html&lt;/code&gt; is more specific than &lt;code&gt;index.html&lt;/code&gt;, but otherwise the two templates are pretty much interchangeable. In other words, they do the same thing – render the homepage.&lt;/p&gt;
&lt;p&gt;In the root of the &lt;code&gt;/content&lt;/code&gt; folder you’ll find a &lt;a href="https://gohugo.io/content-management/sections/" target="_blank" rel="noopener"&gt;content section&lt;/a&gt; called &lt;em&gt;posts&lt;/em&gt;. We know this is a content section primarily because it contains an &lt;code&gt;_index.md&lt;/code&gt; file, which, in this case, is used to add content to a page on our site found at &lt;code&gt;/posts&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;/posts&lt;/code&gt; folder also contains two other files; &lt;code&gt;post-1.md&lt;/code&gt; and &lt;code&gt;post-2.md&lt;/code&gt; and a folder called &lt;code&gt;/post-3&lt;/code&gt;. This demonstrates that you can either store your post content as simple markdown files in the root of a content section, or instead create &lt;a href="https://gohugo.io/content-management/page-bundles/" target="_blank" rel="noopener"&gt;page bundles&lt;/a&gt;, as we see with &lt;code&gt;post-3&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;One advantage of using page bundles is that we can keep resources associated with a specific page alongside the markdown file used for the content of the page. An example of this is the &lt;code&gt;bryce-canyon.jpg&lt;/code&gt; file, found inside the &lt;code&gt;post-3&lt;/code&gt; folder.
These &lt;a href="https://gohugo.io/content-management/page-resources/" target="_blank" rel="noopener"&gt;page resources&lt;/a&gt; can be accessed using the &lt;a href="https://gohugo.io/methods/page/resources/" target="_blank" rel="noopener"&gt;Resources&lt;/a&gt; method (not to be confused with &lt;a href="https://gohugo.io/functions/resources/get/" target="_blank" rel="noopener"&gt;resources functions&lt;/a&gt;) and can be processed in a variety of ways.&lt;/p&gt;
&lt;p&gt;Together, the contents of the &lt;em&gt;posts&lt;/em&gt; section form a &lt;a href="https://gohugo.io/quick-reference/page-collections/" target="_blank" rel="noopener"&gt;collection&lt;/a&gt; of posts. Collections are rendered as a list using &lt;a href="https://gohugo.io/templates/lists/" target="_blank" rel="noopener"&gt;list templates&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;Open &lt;code&gt;layouts/_default/list.html&lt;/code&gt; and you’ll see this in action:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{ define &amp;#34;main&amp;#34; }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ .Title }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h1&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{ .Content }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{ range .Pages }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ .LinkTitle }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{ .Summary }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{ end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{ end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;The default list template is used to list content found within content sections at a URL path that matches the name of the content section. The pages in the content sections are stored in the Page object, so that’s why we find &lt;a href="https://gohugo.io/methods/page/pages/" target="_blank" rel="noopener"&gt;Pages&lt;/a&gt; passed to the &lt;a href="https://gohugo.io/functions/go-template/range/" target="_blank" rel="noopener"&gt;Range function&lt;/a&gt;, which loops through the object and returns a collection of regular pages within the current section.&lt;/p&gt;
&lt;p&gt;As &lt;code&gt;posts&lt;/code&gt; is the only content section inside the default theme content folder, by default the list template is only used to list out the content within this section. The result is a list of blog posts found at &lt;code&gt;/posts&lt;/code&gt; in the browser.&lt;/p&gt;
&lt;h2 id="date-localization"&gt;Date localization&lt;/h2&gt;
&lt;p&gt;Opening &lt;code&gt;layouts/_default/single.html&lt;/code&gt; you’ll find an example of date localization.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ $dateMachine := .Date | time.Format &amp;#34;2006-01-02T15:04:05-07:00&amp;#34; }}
{{ $dateHuman := .Date | time.Format &amp;#34;:date_long&amp;#34; }}
&amp;lt;time datetime=&amp;#34;{{ $dateMachine }}&amp;#34;&amp;gt;{{ $dateHuman }}&amp;lt;/time&amp;gt;
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Here we find a human readable date and a date specifically for crawlers. Both are generated using the &lt;a href="https://gohugo.io/functions/time/format/" target="_blank" rel="noopener"&gt;time.Format function&lt;/a&gt; which returns the date/time found in the front matter of markdown files as a formatted and localized string.&lt;/p&gt;
&lt;p&gt;What this means is that should you decide to expand on the theme by adding &lt;a href="https://gohugo.io/content-management/multilingual/" target="_blank" rel="noopener"&gt;multilingual mode&lt;/a&gt;, dates will render in the correct language, depending on what languages you set up in the site configuration. See the &lt;a href="https://gohugo.io/content-management/multilingual/#dates" target="_blank" rel="noopener"&gt;Date localization&lt;/a&gt; docs for more.&lt;/p&gt;
&lt;h2 id="taxonomies"&gt;Taxonomies&lt;/h2&gt;
&lt;p&gt;Inside the &lt;code&gt;terms.html&lt;/code&gt; partial you’ll find the code used to render tags on single post pages.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- /*
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;For a given taxonomy, renders a list of terms assigned to the page.
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;@context {page} page The current page.
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;@context {string} taxonomy The taxonomy.
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;@example: {{ partial &amp;#34;terms.html&amp;#34; (dict &amp;#34;taxonomy&amp;#34; &amp;#34;tags&amp;#34; &amp;#34;page&amp;#34; .) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;*/}}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- $page := .page }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- $taxonomy := .taxonomy }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- with $page.GetTerms $taxonomy }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $label := (index . 0).Parent.LinkTitle }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ $label }}:&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- range . }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ .LinkTitle }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;div&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Let’s break this down so we understand what’s happening here.&lt;/p&gt;
&lt;p&gt;At the top of the partial file, right below where you see the commented code, two variables have been set up which store the values &lt;code&gt;.page&lt;/code&gt; and &lt;code&gt;.taxonomy&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;As you can see from the commented &lt;code&gt;@example&lt;/code&gt; at the top of the &lt;code&gt;single.html&lt;/code&gt;, the values of these two variables are being passed into the partial using the &lt;a href="https://gohugo.io/functions/collections/dictionary/" target="_blank" rel="noopener"&gt;Dictionary or “dict” function&lt;/a&gt;, which creates a map of key-value pairs. In this case, the value of &lt;code&gt;.taxonomy&lt;/code&gt; is &lt;code&gt;tags&lt;/code&gt;, which is the name of the taxonomy found in the front matter of each &lt;em&gt;post&lt;/em&gt; markdown file. The value of &lt;code&gt;.page&lt;/code&gt; is simply the dot (&lt;code&gt;.&lt;/code&gt;), which holds the context of the page (the data available to it). Without these two variables we have no data to work with in the partial, so they are both very important.&lt;/p&gt;
&lt;p&gt;Directly below where these two variables have been declared we find both variables used.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- with $page.GetTerms $taxonomy }}
...
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Here, &lt;code&gt;$page&lt;/code&gt; gives the &lt;a href="https://gohugo.io/methods/page/getterms/" target="_blank" rel="noopener"&gt;GetTerms&lt;/a&gt; method access to the page context. This step is important, as GetTerms is a page method, so it requires the page context in order to work. &lt;code&gt;$taxonomy&lt;/code&gt; passes the &lt;code&gt;tags&lt;/code&gt; data to the method, so it has data to work with.&lt;/p&gt;
&lt;p&gt;If this were directly inside the &lt;code&gt;single.html&lt;/code&gt; template (where the context is already set to Page by default) and not within a partial, then you could achieve the same results with the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- with .GetTerms &amp;#34;tags&amp;#34; }}
...
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Ok. Now that &lt;code&gt;terms.html&lt;/code&gt; has access to the page context and the &lt;code&gt;tags&lt;/code&gt; data stored in the front matter of the posts, it can render some content.&lt;/p&gt;
&lt;p&gt;Inside the &lt;code&gt;with&lt;/code&gt; statement, where the context has now changed to &lt;code&gt;tags&lt;/code&gt;, first we find the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $label := (index . 0).Parent.LinkTitle }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Here, a variable has been set up called &lt;code&gt;$label&lt;/code&gt; which stores the key associated with the &lt;code&gt;tags&lt;/code&gt; data. In other words, the word “Tags”.&lt;/p&gt;
&lt;p&gt;To grab the key, first the &lt;a href="https://gohugo.io/functions/collections/indexfunction/" target="_blank" rel="noopener"&gt;Index function&lt;/a&gt; is used to get hold of the first value found in the tags data. Notice the dot (&lt;code&gt;.&lt;/code&gt;) after the word &lt;em&gt;index&lt;/em&gt;, this is important as it holds the tags data.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ index . 0 }} // Page(/tags/red)
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Remember, in programming, the first value in an array is 0 and not 1, so 0 is used. If you have a look at the front matter of each post you will find that, in each case, this value is “red”. But “red” isn’t what we’re after here, the goal in fact is to render the key (“Tags”).&lt;/p&gt;
&lt;p&gt;With the &lt;a href="https://gohugo.io/methods/page/parent/" target="_blank" rel="noopener"&gt;Parent page method&lt;/a&gt;, it’s possible to get the Page object of the parent section. In other words &lt;code&gt;tags&lt;/code&gt; instead of &lt;code&gt;/tags/red&lt;/code&gt;.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ (index . 0).Parent }} // Page(/tags)
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;But this would return &lt;code&gt;Page(/tags)&lt;/code&gt;, which isn’t formatted quite right. Instead we want “Tags” as plain text, in title case. This is where the &lt;a href="https://gohugo.io/methods/page/linktitle/" target="_blank" rel="noopener"&gt;LinkTitle&lt;/a&gt; page method comes in.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ (index . 0).Parent.LinkTitle }} // Tags
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Now all that’s left to do is call the variable to render the content, which we find here:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;&amp;lt;div&amp;gt;{{ $label }}:&amp;lt;/div&amp;gt; // Tags:
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;The final bit of logic in this partial should be fairly familiar.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- range . }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ .LinkTitle }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Here, the Range function loops through the &lt;code&gt;tags&lt;/code&gt; data and renders the relative permalink and the title. Simple stuff.&lt;/p&gt;
&lt;p&gt;Now let’s turn our attention to scripts and styles.&lt;/p&gt;
&lt;h2 id="css-and-javascript-inclusion"&gt;CSS and JavaScript inclusion&lt;/h2&gt;
&lt;p&gt;The theme skeleton includes both the CSS and JavaScript in the &lt;code&gt;&amp;lt;head&amp;gt;&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;Opening the &lt;code&gt;baseof.html&lt;/code&gt; template, we can see that the contents of the &lt;code&gt;&amp;lt;head&amp;gt;&lt;/code&gt; is included as a partial.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;head&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{ partial &amp;#34;head.html&amp;#34; . }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;head&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;If we open the &lt;code&gt;head.html&lt;/code&gt; partial we can see that the CSS and JavaScript have also been included as partials, but this time as &lt;a href="https://gohugo.io/templates/partials/#cached-partials" target="_blank" rel="noopener"&gt;cached partials&lt;/a&gt;.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ partialCached &amp;#34;head/css.html&amp;#34; . }}
{{ partialCached &amp;#34;head/js.html&amp;#34; . }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;This means Hugo will cache the result, which will significantly improve performance. It only makes sense to cache partials if they don’t need to be re-rendered.&lt;/p&gt;
&lt;p&gt;If we open up &lt;code&gt;partials/head/css.html&lt;/code&gt;, we find the following code used to render CSS.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- with resources.Get &amp;#34;css/main.css&amp;#34; }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- if eq hugo.Environment &amp;#34;development&amp;#34; }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;link&lt;/span&gt; &lt;span class="na"&gt;rel&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;stylesheet&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- else }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with . | minify | fingerprint }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;link&lt;/span&gt; &lt;span class="na"&gt;rel&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;stylesheet&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;integrity&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .Data.Integrity }}&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;crossorigin&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;anonymous&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;The &lt;a href="https://gohugo.io/functions/resources/get/" target="_blank" rel="noopener"&gt;resources.Get&lt;/a&gt; function returns a global resource from the given path, which in this case is &lt;code&gt;css/main.css&lt;/code&gt;. For this to work correctly, resources need to be stored in the &lt;code&gt;/assets&lt;/code&gt; folder.&lt;/p&gt;
&lt;p&gt;If we head to the &lt;code&gt;/assets&lt;/code&gt; folder, we find &lt;code&gt;css/main.css&lt;/code&gt; as expected:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;body&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mh"&gt;#222&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-family&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;sans-serif&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;line-height&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;1.5&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;max-width&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;768&lt;/span&gt;&lt;span class="kt"&gt;px&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;header&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;border-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;px&lt;/span&gt; &lt;span class="kc"&gt;solid&lt;/span&gt; &lt;span class="mh"&gt;#222&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;footer&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;border-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;px&lt;/span&gt; &lt;span class="kc"&gt;solid&lt;/span&gt; &lt;span class="mh"&gt;#222&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mh"&gt;#00e&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;none&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Heading back to &lt;code&gt;partials/head/css.html&lt;/code&gt;, the next bit of logic to wrap our heads around is the &lt;em&gt;if else&lt;/em&gt; statement.&lt;/p&gt;
&lt;p&gt;Here, the &lt;a href="https://gohugo.io/functions/hugo/environment/" target="_blank" rel="noopener"&gt;hugo.Environment&lt;/a&gt; function is used to qualify whether the current environment is &lt;em&gt;development&lt;/em&gt; or not. In other words, is &lt;code&gt;hugo server&lt;/code&gt; currently running a local development server? If this statement evaluates &lt;em&gt;true&lt;/em&gt;, then the CSS is rendered without any processing. If the statement evaluates &lt;em&gt;false&lt;/em&gt;, then the CSS will be &lt;a href="https://gohugo.io/hugo-pipes/minification/" target="_blank" rel="noopener"&gt;minified&lt;/a&gt; and &lt;a href="https://gohugo.io/hugo-pipes/fingerprint/" target="_blank" rel="noopener"&gt;fingerprinted&lt;/a&gt; with a hash string, which is useful for cache busting when caching assets. Exactly the sort of processing what we want in production.&lt;/p&gt;
&lt;p&gt;If we open up &lt;code&gt;partials/head/js.html&lt;/code&gt;, we find a very similar approached used used to render JavaScript:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- with resources.Get &amp;#34;js/main.js&amp;#34; }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- if eq hugo.Environment &amp;#34;development&amp;#34; }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with . | js.Build }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;script&lt;/span&gt; &lt;span class="na"&gt;src&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;script&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- else }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $opts := dict &amp;#34;minify&amp;#34; true }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with . | js.Build $opts | fingerprint }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;script&lt;/span&gt; &lt;span class="na"&gt;src&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;integrity&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{- .Data.Integrity }}&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;crossorigin&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;anonymous&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;script&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;And similar to the CSS, if we head to the &lt;code&gt;/assets&lt;/code&gt; folder, we find &lt;code&gt;js/main.js&lt;/code&gt;, as expected:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;console.log(&amp;#39;This site was generated by Hugo.&amp;#39;);
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;The main difference here is that JavaScript is processed using &lt;a href="https://gohugo.io/hugo-pipes/js/" target="_blank" rel="noopener"&gt;js.Build&lt;/a&gt;.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- with . | js.Build }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;script&lt;/span&gt; &lt;span class="na"&gt;src&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ .RelPermalink }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;script&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;It also shows how to abstract options to a variable, then include them in a statement:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $opts := dict &amp;#34;minify&amp;#34; true }}
{{- with . | js.Build $opts | fingerprint }}
...
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Which is just another way to express the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- with . | js.Build | minify | fingerprint }}
...
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;With the CSS and JavaScript covered, let’s turn our attention to partials.&lt;/p&gt;
&lt;h2 id="partial-templates"&gt;Partial templates&lt;/h2&gt;
&lt;p&gt;We’ve already touched on cached partials, which are used to render the CSS and JS partials in the theme. These partials are cached because they presumably don’t need to be re-rendered.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ partialCached &amp;#34;head/css.html&amp;#34; . }}
{{ partialCached &amp;#34;head/js.html&amp;#34; . }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;There are two other approaches to partials used in the theme which we haven’t looked at yet – how to work with regular partials (that don’t need to be cached) and how to work with inline partials.&lt;/p&gt;
&lt;p&gt;Regular partials are used when templates need to be re-rendered, usually because of content changes. For example, you wouldn’t want to cache a partial that contained the code that renders the &lt;code&gt;&amp;lt;head&amp;gt;&lt;/code&gt; section of your website, because the chances are high that the &lt;code&gt;&amp;lt;title&amp;gt;&lt;/code&gt; will change on a page-by-page basis.&lt;/p&gt;
&lt;p&gt;In this case, you would include the partial as a regular partial include (without the word &lt;code&gt;Cached&lt;/code&gt; prepending the word partial):&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ partial &amp;#34;head.html&amp;#34; . }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Other than that, there isn’t much difference between the two. Partial templates still live in the &lt;code&gt;layouts/partials&lt;/code&gt; folder and everything works exactly the same.&lt;/p&gt;
&lt;p&gt;Inline partials are different. As the name suggests, Inline partials don’t live inside their own named file within the &lt;code&gt;layouts/partials&lt;/code&gt; folder, but instead live inline within the template in which they are used.&lt;/p&gt;
&lt;p&gt;We can see an example of an inline partial inside the &lt;code&gt;menu.html&lt;/code&gt; partial.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;// Include the partial code
{{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
// Define the partial code
{{- define &amp;#34;partials/inline/menu/walk.html&amp;#34; }}
...
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;I’ve taken the code above directly from this file and simplified it to show how inline partials work.&lt;/p&gt;
&lt;p&gt;First the partial is defined with the &lt;a href="https://gohugo.io/functions/go-template/define/" target="_blank" rel="noopener"&gt;define function&lt;/a&gt;, then named within dumb quotes. Following this, the code to be added as a partial is included (which I’ve omitted here for clarity). Finally, as this is logic it’s important to close it out with &lt;code&gt;{{ end }}&lt;/code&gt; at the end. You’ll notice this works in the exact same way as &lt;a href="https://gohugo.io/functions/go-template/block/" target="_blank" rel="noopener"&gt;defining a block&lt;/a&gt;.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;// Define the partial code
{{- define &amp;#34;partials/inline/menu/walk.html&amp;#34; }}
S...
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Now the partial is defined, it can be included anywhere in the same template as you would a regular partial.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;// Include the partial code
{{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;It’s important to remember that template namespace is global, so you need to make sure that the names of your partials are unique to avoid conflicts.&lt;/p&gt;
&lt;p&gt;Now we’ve warmed up, let’s tackle what’s possibly the most complicated part of this starter theme – the logic found inside the &lt;code&gt;menu.html&lt;/code&gt; template.&lt;/p&gt;
&lt;h2 id="menus"&gt;Menus&lt;/h2&gt;
&lt;p&gt;In a project using the Hugo starter theme, run &lt;code&gt;hugo server&lt;/code&gt; then head to http://localhost:1313/ in a browser and you’ll see the menu right below the site title at the top of the page.&lt;/p&gt;
&lt;p&gt;Assuming you haven’t changed any of the menu items from their defaults, the menu consists of three links; Home, Posts and Tags. The code responsible for this menu of links can be found in the &lt;code&gt;menu.html&lt;/code&gt; partial.&lt;/p&gt;
&lt;h3 id="including-the-menu-and-passing-data-to-the-partial"&gt;Including the menu and passing data to the partial&lt;/h3&gt;
&lt;p&gt;Before we jump into the &lt;code&gt;menu.html&lt;/code&gt; partial, first let’s take a look inside the &lt;code&gt;header.html&lt;/code&gt; partial, as this is where the &lt;code&gt;menu.html&lt;/code&gt; partial is included in the templates.&lt;/p&gt;
&lt;p&gt;Open the &lt;code&gt;header.html&lt;/code&gt; partial and you’ll find the following line of code:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ partial &amp;#34;menu.html&amp;#34; (dict &amp;#34;menuID&amp;#34; &amp;#34;main&amp;#34; &amp;#34;page&amp;#34; .) }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;If you read the previous section on partials then the code above will look familiar. To render the partial, the &lt;a href="https://gohugo.io/functions/partials/include/" target="_blank" rel="noopener"&gt;partial function&lt;/a&gt; is used followed by the name of the partial within double quotes.&lt;/p&gt;
&lt;p&gt;In this instance, we also find the following appended to the code:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;(dict &amp;#34;menuID&amp;#34; &amp;#34;main&amp;#34; &amp;#34;page&amp;#34; .)
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;So what’s going on here?&lt;/p&gt;
&lt;p&gt;In Hugo projects, the &lt;a href="https://gohugo.io/functions/collections/dictionary/" target="_blank" rel="noopener"&gt;Dictionary function&lt;/a&gt; – or its alias form &lt;em&gt;dict&lt;/em&gt;, as we see here – can be used to return a map of given key-value pairs. When used in a partial include like this, &lt;em&gt;dict&lt;/em&gt; passes a map of key-value pairs into the partial (in this case &lt;code&gt;menu.html&lt;/code&gt; ), so that the data is available to use from inside the partial. In this case, the first key in the map is &lt;code&gt;menuID&lt;/code&gt; which holds the value of &lt;code&gt;main&lt;/code&gt; and corresponds to the menu ID used to identify the menu items found within the &lt;code&gt;hugo.toml&lt;/code&gt;:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-toml" data-lang="toml"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[[&lt;/span&gt;&lt;span class="nx"&gt;menus&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;main&lt;/span&gt;&lt;span class="p"&gt;]]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;Home&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;pageRef&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;/&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;weight&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="mi"&gt;10&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[[&lt;/span&gt;&lt;span class="nx"&gt;menus&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;main&lt;/span&gt;&lt;span class="p"&gt;]]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;Posts&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;pageRef&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;/posts&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;weight&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="mi"&gt;20&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[[&lt;/span&gt;&lt;span class="nx"&gt;menus&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;main&lt;/span&gt;&lt;span class="p"&gt;]]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;Tags&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;pageRef&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;/tags&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;weight&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="mi"&gt;30&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Of course, if, for example, we had a &lt;em&gt;footer&lt;/em&gt; menu instead of a &lt;em&gt;main&lt;/em&gt; menu, then the value of &lt;code&gt;menuID&lt;/code&gt; would be &lt;em&gt;footer&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;Passing the menu ID to the partial is important as it allows the &lt;code&gt;menu.html&lt;/code&gt; partial to render the &lt;em&gt;main&lt;/em&gt; menu items. Without specifying ”main” Hugo wouldn’t find any menu items to render on the page.&lt;/p&gt;
&lt;p&gt;The second key in the &lt;em&gt;dict&lt;/em&gt; map is &lt;code&gt;page&lt;/code&gt; and this holds the dot (&lt;code&gt;.&lt;/code&gt;) as its value. In this case, the dot passes the current page context to the &lt;code&gt;menu.html&lt;/code&gt; partial. Without passing the page context to the partial, it wouldn’t be possible to work with any of the &lt;a href="https://gohugo.io/methods/pages/" target="_blank" rel="noopener"&gt;page methods&lt;/a&gt; from inside the partial. So menu features which typically require page methods, such as labelling the current page item as “active”, wouldn’t be possible.&lt;/p&gt;
&lt;p&gt;Now we have a grasp of how to include the menu and passing data to the partial, let’s head over to the &lt;code&gt;menu.html&lt;/code&gt; partial itself and figure out how this menu is working.&lt;/p&gt;
&lt;h3 id="rendering-the-inline-menu-partial"&gt;Rendering the inline menu partial&lt;/h3&gt;
&lt;p&gt;At the top of the &lt;code&gt;menu.html&lt;/code&gt; partial, right below the commented code, you will notice two variables have been declared. Both of which should look familiar. The first variable, &lt;code&gt;$page&lt;/code&gt;, stores the page context and the second, &lt;code&gt;$menuID&lt;/code&gt;, stores the menu ID (main). These are storing the data passed into the partial via the dict function, as we just discovered.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $page := .page }}
{{- $menuID := .menuID }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Now it’s possible to work with this data anywhere within the &lt;code&gt;menu.html&lt;/code&gt; template.&lt;/p&gt;
&lt;p&gt;Directly below these two variable definition we find the following code:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- with index site.Menus $menuID }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;nav&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;nav&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;This is used to render the navigation, part of which has been abstracted to an inline partial, which we will look at shortly.&lt;/p&gt;
&lt;p&gt;Before we do that, first let’s get a better understanding of what’s going on in the code above.&lt;/p&gt;
&lt;p&gt;Here, the &lt;a href="https://gohugo.io/functions/go-template/with/" target="_blank" rel="noopener"&gt;With function&lt;/a&gt; is used to render the code inside the expression &lt;em&gt;if&lt;/em&gt; &lt;code&gt;site.Menus&lt;/code&gt; and &lt;code&gt;$menuID&lt;/code&gt; returns &lt;em&gt;true&lt;/em&gt;. We know there are menu items in the &lt;code&gt;hugo.toml&lt;/code&gt; file and that the partial has access to &lt;code&gt;$menuID&lt;/code&gt;, as it’s been passed in, so we expect the expression to return &lt;em&gt;true&lt;/em&gt;. If, let’s say, the menu items were removed, along with the menu ID, then we would expect the expression to return &lt;em&gt;false&lt;/em&gt; and nothing inside the expression would render. Essentially, the With function creates a conditional statement and changes the context to whatever is passed to it.&lt;/p&gt;
&lt;p&gt;Notice the &lt;a href="https://gohugo.io/functions/collections/indexfunction/" target="_blank" rel="noopener"&gt;Index function&lt;/a&gt; is also included. This is used to return the value of &lt;code&gt;$menuID&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;Another way to think about this is the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- with index site.Menus &amp;#34;main&amp;#34; }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Without the Index function, it would only be possible to return a hard coded value, as you’ve probably seen in menus found in other themes:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- with site.Menus.main }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;While this approach works just the same, the issue is that it wouldn’t allow for reusability of the &lt;code&gt;menu.html&lt;/code&gt; partial (for example if we decided to also add a footer menu to our site some time in the future), so that’s why we find the &lt;code&gt;$menuID&lt;/code&gt; stored in a variable.&lt;/p&gt;
&lt;p&gt;Before I digress any further, let’s turn our attention to the code that the With statement renders:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;nav&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;nav&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Here we find a &lt;code&gt;&amp;lt;nav&amp;gt;&lt;/code&gt; element and nested &lt;code&gt;&amp;lt;ul&amp;gt;&lt;/code&gt; which is typically used in most menus. The main difference to what you usually might expect is that the code we’d typically find within the &lt;code&gt;&amp;lt;ul&amp;gt;&lt;/code&gt; element has been abstracted into an inline partial called &lt;code&gt;inline/menu/walk.html&lt;/code&gt;. Remember the one we looked at in the partials section earlier?.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Before we look at the code within this inline partial, first let’s take a look at the data that’s passed to the inline partial, again via &lt;em&gt;dict&lt;/em&gt;, a.k.a the Dictionary function.&lt;/p&gt;
&lt;p&gt;The first key in the dict map is &lt;code&gt;page&lt;/code&gt;, which holds the value of our &lt;code&gt;$page&lt;/code&gt; variable. If you remember, &lt;code&gt;$page&lt;/code&gt; holds the page context. By passing the page context to the inline partial, page methods can be used within it. It can be confusing when we get into partial inception like this, but the important thing to remember is that data and/or context needs to be passed to every partial that requires it. That includes inline partials.&lt;/p&gt;
&lt;p&gt;The second key in the &lt;em&gt;dict&lt;/em&gt; map is &lt;code&gt;menuEntries&lt;/code&gt;, which is an arbitrary name used to describe the value it holds - the dot (&lt;code&gt;.&lt;/code&gt;).&lt;/p&gt;
&lt;p&gt;It’s important to note that the With function binds context (the dot) to the expression. What this means is that as soon as you use With, the context changes to whatever you pass in to the function. In this case, the context is no longer Page, but instead &lt;code&gt;site.Menus&lt;/code&gt;, and more specifically the &lt;code&gt;main&lt;/code&gt; menu. So the value of &lt;code&gt;menuEntries&lt;/code&gt; actually holds the structured main menu data that we find inside the &lt;code&gt;hugo.toml&lt;/code&gt; file. The inline partial now has access to the main menu data.&lt;/p&gt;
&lt;p&gt;Ok, it’s the final push. Now let’s look at the inline partial itself.&lt;/p&gt;
&lt;h3 id="defining-the-inline-menu-partial"&gt;Defining the inline menu partial&lt;/h3&gt;
&lt;p&gt;We’ve already looked at how to define an inline partial in the partials section of this post, so I won’t go over that again. What we‘re interested in now is the code inside the partial definition found in the &lt;code&gt;menu.html&lt;/code&gt; partial.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- define &amp;#34;partials/inline/menu/walk.html&amp;#34; }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $page := .page }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- range .menuEntries }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $attrs := dict &amp;#34;href&amp;#34; .URL }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- if $page.IsMenuCurrent .Menu . }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;active&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;page&amp;#34;) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- else if $page.HasMenuCurrent .Menu .}}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;ancestor&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;true&amp;#34;) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $name := .Name }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with .Identifier }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with T . }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- $name = . }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;range&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;k&lt;/span&gt;&lt;span class="err"&gt;,&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="na"&gt;:&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt; &lt;span class="s"&gt;$attrs&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;with&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;printf&lt;/span&gt; &lt;span class="err"&gt;&amp;#34;&lt;/span&gt; &lt;span class="err"&gt;%&lt;/span&gt;&lt;span class="na"&gt;s&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;%q&amp;#34;&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;k&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="err"&gt;|&lt;/span&gt; &lt;span class="na"&gt;safeHTMLAttr&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;end&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;end&lt;/span&gt; &lt;span class="na"&gt;-&lt;/span&gt;&lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ $name }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with .Children }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Quite a bit of complicated looking code here, but it’s fairly straight forward once you get your head around it. I’ll do my best to walk you through it.&lt;/p&gt;
&lt;p&gt;Before we begin, the important thing to realise is that all this code is actually doing is rendering the menu list items, the links within these list items and child menu items (should they exist). The brunt of complexity in this code is the logic which builds the link attributes and translates the name of the menu item (should you decide to set up multilingual mode on your site).&lt;/p&gt;
&lt;p&gt;To make sense of all this, let’s start at the top of the partial definition, on the line right below where we find the define function in use.&lt;/p&gt;
&lt;p&gt;Here we find a variable called &lt;code&gt;$page&lt;/code&gt;, which stores the value &lt;code&gt;.page&lt;/code&gt;, a.k.a the
the page context, which has been passed into the partial via the dict function found in the partial include we looked at previously.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt; {{- $page := .page }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;If you follow &lt;code&gt;.page&lt;/code&gt; backwards you’ll notice that it’s been passed all the way down from the &lt;code&gt;header.html&lt;/code&gt; partial, where the context is Page. With page context stored inside &lt;code&gt;$page&lt;/code&gt;, it’s now available to the inline partial.&lt;/p&gt;
&lt;p&gt;Next we find a &lt;a href="https://gohugo.io/functions/go-template/range/" target="_blank" rel="noopener"&gt;Range function&lt;/a&gt;, which is used to iterate over collections, in this case &lt;code&gt;.menuEntries&lt;/code&gt;.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- range .menuEntries }}
...
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;If you remember from the previous section, &lt;code&gt;.menuEntries&lt;/code&gt; stores the &lt;em&gt;main&lt;/em&gt; menu data, which was also passed into the inline partial via the dict function in the partial include. So here Range is iterating over the menu items found in the &lt;code&gt;hugo.toml&lt;/code&gt; config file, similar to how a &lt;em&gt;forEach&lt;/em&gt; might in JavaScript.&lt;/p&gt;
&lt;p&gt;Now the context has changed to the menu items, all data associated with the menu items is available. The item &lt;code&gt;name&lt;/code&gt;, &lt;code&gt;identifier&lt;/code&gt; and &lt;code&gt;URL&lt;/code&gt;, for example, can all be used inside the inline partial.&lt;/p&gt;
&lt;p&gt;With that in mind, let’s look at the next chunk of code in the inline partial.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $attrs := dict &amp;#34;href&amp;#34; .URL }}
{{- if $page.IsMenuCurrent .Menu . }}
{{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;active&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;page&amp;#34;) }}
{{- else if $page.HasMenuCurrent .Menu . }}
{{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;ancestor&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;true&amp;#34;) }}
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;This is the logic that determines which attributes should be added to the&lt;code&gt;&amp;lt;a&amp;gt;&lt;/code&gt; element found further down in the partial definition. We’ll look at this anchor tag in more detail, but first let’s breakdown this logic.&lt;/p&gt;
&lt;p&gt;On the first line in the code above a variable called &lt;code&gt;$attrs&lt;/code&gt; has been declared which stores &lt;code&gt;.URL&lt;/code&gt; as the value.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $attrs := dict &amp;#34;href&amp;#34; .URL }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;&lt;a href="https://gohugo.io/methods/menu-entry/url/" target="_blank" rel="noopener"&gt;URL&lt;/a&gt; returns the relative permalink of the page associated with the given menu entry. In this case, the Dictionary function (dict) is used to create a map with a key called “href” used to label the URL. The reason for this is that &lt;code&gt;href&lt;/code&gt; is the HTML attribute used to render URL’s in links, so by providing &lt;em&gt;href&lt;/em&gt; as the key, Hugo will be able to build the link attributes correctly.&lt;/p&gt;
&lt;p&gt;Next we find the logic itself:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- if $page.IsMenuCurrent .Menu . }}
{{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;active&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;page&amp;#34;) }}
{{- else if $page.HasMenuCurrent .Menu . }}
{{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;ancestor&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;true&amp;#34;) }}
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Which we might interpret in plain english as the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ if this evaluates true }}
{{ do this }}
{{ otherwise, if this evaluates true }}
{{ do this instead }}
{{ end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;In the first part of the logic, &lt;a href="https://gohugo.io/methods/page/ismenucurrent/" target="_blank" rel="noopener"&gt;IsMenuCurrent&lt;/a&gt; is used to check if the current page matches the current menu item. Notice &lt;code&gt;IsMenuCurrent&lt;/code&gt; is appended to the &lt;code&gt;$page&lt;/code&gt; variable from earlier. This is important as &lt;code&gt;IsMenuCurrent&lt;/code&gt; is a page method, so will only work within the Page context. &lt;code&gt;$page&lt;/code&gt; provides the context.&lt;/p&gt;
&lt;p&gt;OK, so if the statement on the first line in the code above evaluates &lt;em&gt;true&lt;/em&gt;, then the &lt;code&gt;$attrs&lt;/code&gt; variable is reassigned with a new value:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;active&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;page&amp;#34;) }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Notice &lt;code&gt;=&lt;/code&gt; is used to assign a new value to the variable, whereas when the variable was initialised, &lt;code&gt;:=&lt;/code&gt; was used. This is an important distinction – &lt;code&gt;:=&lt;/code&gt; is used to initialise a variable and &lt;code&gt;=&lt;/code&gt; is used to update or reassign its value.&lt;/p&gt;
&lt;p&gt;So what is the value that’s being assigned? It’s two more attributes; a &lt;code&gt;class&lt;/code&gt; with the value of &lt;code&gt;active&lt;/code&gt; and &lt;code&gt;aria-current&lt;/code&gt; with the value of &lt;code&gt;page&lt;/code&gt;. As before, both are passed as a map of key-value pairs, using the Dict function. Notice the &lt;a href="https://gohugo.io/functions/collections/merge/" target="_blank" rel="noopener"&gt;Merge function&lt;/a&gt; is used to merge this new map with the original &lt;code&gt;$attrs&lt;/code&gt; map, rather that replace it entirely.&lt;/p&gt;
&lt;p&gt;Again, in plain english we might interpret this as the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ if the current page matches the current menu item }}
{{ update $attrs from `dict &amp;#34;href&amp;#34; .URL` to `dict &amp;#34;href&amp;#34; .URL &amp;#34;class&amp;#34; &amp;#34;active&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;page&amp;#34;` }}
{{ otherwise, if this evaluates true }}
{{ do this instead }}
{{ end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Now let’s take a stab at the second part of this logic. This should be much easier, now that we have a good grasp of what’s going on in the first part.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- else if $page.HasMenuCurrent .Menu . }}
{{- $attrs = merge $attrs (dict &amp;#34;class&amp;#34; &amp;#34;ancestor&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;true&amp;#34;) }}
...
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Here, we we find an else statement with a new condition. This is what will happen if the first statement evaluates &lt;em&gt;false&lt;/em&gt;. This time, the &lt;a href="https://gohugo.io/methods/page/hasmenucurrent/" target="_blank" rel="noopener"&gt;HasMenuCurrent&lt;/a&gt; is passed to check whether the page matches the page associated with one of the child menu entries. If this statement evaluates &lt;em&gt;true&lt;/em&gt;, then similar to how we saw before, &lt;code&gt;$attrs&lt;/code&gt; is updated to include a slightly different map of key-value pairs, then merged with the original &lt;code&gt;$attrs&lt;/code&gt; map.&lt;/p&gt;
&lt;p&gt;Let’s look at this in plain english once again:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{ if the current page matches the current menu item }}
{{ update $attrs from `dict &amp;#34;href&amp;#34; .URL` to `dict &amp;#34;href&amp;#34; .URL &amp;#34;class&amp;#34; &amp;#34;active&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;page&amp;#34;` }}
{{ otherwise, if the page matches the page associated with one of the child menu entries }}
{{ update $attrs from `dict &amp;#34;href&amp;#34; .URL` to `dict &amp;#34;href&amp;#34; .URL &amp;#34;class&amp;#34; &amp;#34;ancestor&amp;#34; &amp;#34;aria-current&amp;#34; &amp;#34;true&amp;#34;` }}
{{ end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;At this point you may have realised that, by default the starter theme doesn’t have any child menu entries. So you’d be right in thinking that the second part of this logic won’t ever evaluate &lt;em&gt;true&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;That is, unless we add a child menu item to the menu configuration found in the &lt;code&gt;hugo.toml&lt;/code&gt; file. Take the below for example.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-toml" data-lang="toml"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[[&lt;/span&gt;&lt;span class="nx"&gt;menus&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;main&lt;/span&gt;&lt;span class="p"&gt;]]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;Posts&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;pageRef&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;/posts&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;weight&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="mi"&gt;20&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;identifier&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;posts&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[[&lt;/span&gt;&lt;span class="nx"&gt;menus&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;main&lt;/span&gt;&lt;span class="p"&gt;]]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;Post 1&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;pageRef&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;/posts/post-1&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nx"&gt;parent&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s1"&gt;&amp;#39;posts&amp;#39;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Notice we’ve added an &lt;code&gt;identifier&lt;/code&gt; key to one of the menu items, in this case the &lt;code&gt;posts&lt;/code&gt; item. The value matches the name of the item, but could be anything. Next we add a new menu item and add a &lt;code&gt;parent&lt;/code&gt; key to it with a value that matches the &lt;code&gt;identifier&lt;/code&gt; of the menu item we want it to be the child of.&lt;/p&gt;
&lt;p&gt;With this new menu configuration in place, the second statement in the logic in question will now evaluate &lt;em&gt;true&lt;/em&gt; for the &lt;em&gt;Posts&lt;/em&gt; menu item.&lt;/p&gt;
&lt;p&gt;It’s important to realise that, at this stage, all the logic is doing is updating the value of &lt;code&gt;$attrs&lt;/code&gt; variable, depending on how each statement evaluates. The attributes haven’t been applied to the link tag yet, where we would usually expect to find them. We’ll get to the part where this happens shortly.&lt;/p&gt;
&lt;p&gt;First, let’s take a brief look at the following code, found inside the &lt;code&gt;menu.html&lt;/code&gt; partial, directly below the logic we’ve just addressed.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;{{- $name := .Name }}
{{- with .Identifier }}
{{- with T . }}
{{- $name = . }}
{{- end }}
{{- end }}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;First we find a new variable, &lt;code&gt;$name&lt;/code&gt;, which stores the value of &lt;code&gt;.Name&lt;/code&gt;. As we’re still inside the Range, the current context is still &lt;code&gt;.menuEntries&lt;/code&gt; (our menu items). Therefore, it’s clear that &lt;code&gt;.Name&lt;/code&gt; is the name of our menu item, taken from our menu object in &lt;code&gt;hugo.toml&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;Next we find a &lt;a href="https://gohugo.io/functions/go-template/with/" target="_blank" rel="noopener"&gt;With function&lt;/a&gt; which changes the context to whatever is passed into it and will execute if the expression returns &lt;em&gt;true&lt;/em&gt;. In this case, &lt;code&gt;.Identifier&lt;/code&gt; is passed in. By default, no menu item in the menu configuration includes an &lt;code&gt;Identifier&lt;/code&gt;, so this is our first clue to what’s happening here – absolutely nothing, unless an Identifier is added to any of the menu items.&lt;/p&gt;
&lt;p&gt;Within this logic we find another With function. This time the dot (a.k.a the current context) is passed in, which remains unchanged – the &lt;code&gt;.Identifier&lt;/code&gt;. Here the &lt;a href="https://gohugo.io/functions/lang/translate/" target="_blank" rel="noopener"&gt;Translate function&lt;/a&gt; is used (in its alias form “T”) to translate the Identifier value, should it exist. Here’s our second clue – this logic will only execute if we decided to set up a &lt;a href="https://gohugo.io/content-management/content-adapters/#multilingual-sites" target="_blank" rel="noopener"&gt;multilingual site&lt;/a&gt; and create translation tables for our content. You can learn more about &lt;a href="https://gohugo.io/functions/lang/translate/#simple-translations" target="_blank" rel="noopener"&gt;creating simple translations&lt;/a&gt; in the Hugo docs, so I won’t go into any more detail about that now. All we need to understand right now is that &lt;code&gt;.Identifier&lt;/code&gt; is used to identify the content to be translated.&lt;/p&gt;
&lt;p&gt;Finally, if the two With expressions evaluate &lt;em&gt;true&lt;/em&gt;, then the value of the &lt;code&gt;$name&lt;/code&gt; variable is reassigned (using &lt;code&gt;=&lt;/code&gt;) to the translated menu item.&lt;/p&gt;
&lt;p&gt;As with the &lt;code&gt;$attrs&lt;/code&gt; variable, which we looked at earlier, it’s important to realise that all this logic does is conditionally update the value of the &lt;code&gt;$name&lt;/code&gt; variable. It’s doesn’t actually manipulate or apply anything to the HTML found in the theme. Instead, it simply prepares these variables so they are ready to be used.&lt;/p&gt;
&lt;p&gt;Now we’ll look at how these variables are actually applied to the HTML in the theme. To do that, let’s consider the list item and everything found within it.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;range&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;k&lt;/span&gt;&lt;span class="err"&gt;,&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="na"&gt;:&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt; &lt;span class="s"&gt;$attrs&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;with&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;printf&lt;/span&gt; &lt;span class="err"&gt;&amp;#34;&lt;/span&gt; &lt;span class="err"&gt;%&lt;/span&gt;&lt;span class="na"&gt;s&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;%q&amp;#34;&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;k&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="err"&gt;|&lt;/span&gt; &lt;span class="na"&gt;safeHTMLAttr&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;end&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;end&lt;/span&gt; &lt;span class="na"&gt;-&lt;/span&gt;&lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ $name }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- with .Children }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;li&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;The first thing to point out is we’re still inside the Range here and the context is still &lt;code&gt;.menuEntries&lt;/code&gt; (the menu items). So we’re expecting this code to return a list item for each menu item in the menu configuration found inside &lt;code&gt;hugo.toml&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;Let’s take a closer look at the anchor tag in the code above.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;range&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;k&lt;/span&gt;&lt;span class="err"&gt;,&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="na"&gt;:&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt; &lt;span class="s"&gt;$attrs&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;with&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;printf&lt;/span&gt; &lt;span class="err"&gt;&amp;#34;&lt;/span&gt; &lt;span class="err"&gt;%&lt;/span&gt;&lt;span class="na"&gt;s&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;%q&amp;#34;&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;k&lt;/span&gt; &lt;span class="err"&gt;$&lt;/span&gt;&lt;span class="na"&gt;v&lt;/span&gt; &lt;span class="err"&gt;|&lt;/span&gt; &lt;span class="na"&gt;safeHTMLAttr&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;end&lt;/span&gt; &lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;{{&lt;/span&gt;&lt;span class="na"&gt;-&lt;/span&gt; &lt;span class="na"&gt;end&lt;/span&gt; &lt;span class="na"&gt;-&lt;/span&gt;&lt;span class="err"&gt;}}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;{{ $name }}&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;First we find the logic which builds the link attributes held in the &lt;code&gt;$attrs&lt;/code&gt; variable. For this to work, &lt;code&gt;$attrs&lt;/code&gt; is passed into the Range function which &lt;a href="https://gohugo.io/functions/go-template/range/#maps" target="_blank" rel="noopener"&gt;ranges over the map&lt;/a&gt; of data and stores it in two variables &lt;code&gt;$k&lt;/code&gt; and &lt;code&gt;$v&lt;/code&gt;. We can assume these variables represent the &lt;em&gt;key&lt;/em&gt; and the &lt;em&gt;value&lt;/em&gt;.&lt;/p&gt;
&lt;p&gt;The With function then changes the context to the value and is used to ensure this code only executes if a value exists. If the With expression evaluates &lt;em&gt;true&lt;/em&gt;, the &lt;a href="https://gohugo.io/functions/fmt/printf/" target="_blank" rel="noopener"&gt;fmt.Printf function&lt;/a&gt; is then used to format the attributes correctly. It’s worth taking a look at &lt;a href="https://pkg.go.dev/fmt#hdr-Printing" target="_blank" rel="noopener"&gt;Go’s Fmt package&lt;/a&gt;, specifically the printing verbs &lt;code&gt;%s&lt;/code&gt; and &lt;code&gt;%q&lt;/code&gt; to better understand how this is comes together.&lt;/p&gt;
&lt;p&gt;With the attributes formatted correctly, the &lt;a href="https://gohugo.io/functions/safe/htmlattr/" target="_blank" rel="noopener"&gt;safeHTMLAttr function&lt;/a&gt; is piped to declare that the key-value pairs are safe HTML attributes and ensure everything renders without errors.&lt;/p&gt;
&lt;p&gt;The final thing to note in the anchor tag is the &lt;code&gt;$name&lt;/code&gt; variable, which is used to render the link text. Simple stuff.&lt;/p&gt;
&lt;p&gt;All that’s left to look at now before we wrap up this breakdown of the &lt;code&gt;menu.html&lt;/code&gt; partial is the following:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- with .Children }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; {{- partial &amp;#34;inline/menu/walk.html&amp;#34; (dict &amp;#34;page&amp;#34; $page &amp;#34;menuEntries&amp;#34; .) }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{- end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;The big give away here is the &lt;a href="https://gohugo.io/methods/menu-entry/children/" target="_blank" rel="noopener"&gt;Children function&lt;/a&gt;, which is used to return a collection of child menu entries, should any exist. As we established earlier, no child menu items exist in the theme by default, so we don’t expect this code the execute. However, should we add child menu items to the theme, then this code would run and build a nested unordered list containing existing child list items.&lt;/p&gt;
&lt;p&gt;Notice that these potential list items are rendered via the inline partial that we’ve just been dissecting. What this tells us is that you can include an inline partial within itself, again performing a partial inception of sorts, should the need present itself, as it has here. This is a great example of why it makes sense to build partials with flexibility and reusability in mind.&lt;/p&gt;
&lt;p&gt;The &lt;code&gt;menu.html&lt;/code&gt; partial is easily the most complicated part of the Hugo starter theme, so don’t stress too much if you’re still struggling to get your head around it. I’ve written about &lt;a href="https://harrycresswell.com/writing/menus-in-hugo/" target="_blank" rel="noopener"&gt;working with menus in Hugo&lt;/a&gt; in detail, so check that post out if you’re looking for a slightly easier implementation.&lt;/p&gt;
&lt;h2 id="final-words"&gt;Final words&lt;/h2&gt;
&lt;p&gt;Great effort if you’ve made it this far. I wasn’t planning on writing over seven thousand words about Hugo improvements but here we are.&lt;/p&gt;
&lt;p&gt;I want to wrap this one up by saying that it’s great to see more emphasis on building themes from scratch, or at least, building sites by using a functional barebones theme as a starting point. It might seem like a trivial thing, but this is a big step in the right direction for Hugo development.&lt;/p&gt;
&lt;p&gt;Before now, installing a theme from &lt;a href="https://themes.gohugo.io/" target="_blank" rel="noopener"&gt;Hugo Themes&lt;/a&gt; often felt like the recommended next step after setting up a new site. While this might make sense if you want to get a Hugo site up and running quickly, personally I’m not sure if it’s always the most sensible approach. Existing Hugo themes are typically built by experienced developers. Often they’re complex and not the most beginner friendly.&lt;/p&gt;
&lt;p&gt;If you really want to learn Hugo then nothing beats building your own theme. My hope is that these changes will encourage more of us to do just that.&lt;/p&gt;
&lt;p&gt;Hopefully this post has helped you get your head around some of the complexities of the default starter theme and has provided you with some encouragement to give theme development a go.&lt;/p&gt;
&lt;p&gt;If you spotted any mistakes whilst reading this post then please let me know and I will do my best to rectify them.&lt;/p&gt;

&lt;p&gt;&lt;a href="https://rknight.me/blog/slash-pages/" target="_blank" rel="noopener"&gt;“Slash pages”&lt;/a&gt; is a great phrase for root-level pages usually found on personal websites. I’m into it.&lt;/p&gt;
&lt;p&gt;Well done to &lt;a href="https://calebhearth.com/" target="_blank" rel="noopener"&gt;Caleb Hearth&lt;/a&gt; and &lt;a href="https://shellsharks.com/" target="_blank" rel="noopener"&gt;Shellsharks&lt;/a&gt; for coining the phrase. Also Brandon for building &lt;a href="https://slashpages.net/" target="_blank" rel="noopener"&gt;slashpages.net&lt;/a&gt;, which lists the most common slash pages and provides a clear definition:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;Slash pages are common pages you can add to your website, usually with a standard, root-level slug like /now, /about, or /uses. They tend to describe the individual behind the site and are distinguishing characteristics of the IndieWeb.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;Annie’s idea of creating &lt;a href="https://weblog.anniegreens.lol/2024/05/a-slashes-page-for-my-slash-pages" target="_blank" rel="noopener"&gt;a slashes page for your slash pages&lt;/a&gt; is also really clever.&lt;/p&gt;
&lt;p&gt;It’s not always clear which slash pages exist when you visit a website. Unless they’re all listed in a navigation somewhere – which invariably they aren’t. So listing them on their own dedicated page is extremely helpful.&lt;/p&gt;
&lt;p&gt;Now we can add our &lt;a href="https://harrycresswell.com/slashes"&gt;/slashes&lt;/a&gt; page somewhere in our site navigation instead.&lt;/p&gt;
&lt;p&gt;I like this idea a lot. It give visitors a quick way to access all the slash pages on your site (some of which may otherwise be missed). It also solves the problem of cluttering your navigation with too many links. Great stuff.&lt;/p&gt;
&lt;p&gt;Thanks to &lt;a href="https://manuelmoreale.com/slashes" target="_blank" rel="noopener"&gt;Manu&lt;/a&gt;, for putting me onto this, and &lt;a href="https://www.brycewray.com/posts/2024/06/slash-pages-existing-new/" target="_blank" rel="noopener"&gt;Bryce&lt;/a&gt; for encouraging me to revisit my existing slash pages and prune back the dead growth.&lt;/p&gt;

&lt;p&gt;It’s encouraging to see folks declaring their work &lt;em&gt;AI-free&lt;/em&gt; or &lt;em&gt;human-generated&lt;/em&gt;, in some form or another.&lt;/p&gt;
&lt;p&gt;Jared White has pledged that &lt;a href="https://www.whitefusion.studio/#stop-publicizing-progress-without-committing-to-ethics" target="_blank" rel="noopener"&gt;everything his web design studio creates will be 100% AI-free&lt;/a&gt; going forward:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;Our pledge to you in 2024 and beyond:&lt;/strong&gt; 100% &lt;mark&gt;AI-free&lt;/mark&gt; productions. All the solutions we provide are of the finest &lt;mark&gt;human-grade quality&lt;/mark&gt;. We’re not afraid of &lt;em&gt;progress&lt;/em&gt;! But we’re not buying the &lt;em&gt;bullshit&lt;/em&gt;. Read more about our &lt;a href="https://www.whitefusion.studio/ai-ethics" target="_blank" rel="noopener"&gt;AI Ethical Framework&lt;/a&gt; we’ve laid out which presents the minimum threshold required for us to evaluate any “generative AI” toolchain.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;a href="https://vhbelvadi.com/" target="_blank" rel="noopener"&gt;Venkatram&lt;/a&gt; has taken another approach, by adding the following statement to the footer of his website:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;Climate friendly and completely AI-free.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;a href="https://weblog.anniegreens.lol/" target="_blank" rel="noopener"&gt;Annie Sturdivant&lt;/a&gt; has done something similar, also adding a statement in the footer of her website:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;Handcrafted by a human.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;a href="https://notbyai.fyi/" target="_blank" rel="noopener"&gt;Not by AI&lt;/a&gt; takes this idea a step further by offering a “produced by a human” badge that you can add to your work. You’ll qualify as long as your work isn‘t generated by AI or at least 90% of it is created by humans.&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;“Not By AI badges are created to encourage more humans to produce original content and help users identify human-generated content.”&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;According the the &lt;a href="https://notbyai.fyi/" target="_blank" rel="noopener"&gt;Not by AI&lt;/a&gt; website, this is important because:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;Artificial Intelligence (AI) is trained using human-created content. If humans stop producing new content and rely solely on AI, online content across the world may become repetitive and stagnant.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;Makes sense. Though you may argue that AI will simply augment human creativity – just like any other creative tool – rather than stagnate it. I suppose that depends on the degree to which we embrace it.&lt;/p&gt;
&lt;p&gt;What you can’t argue with is the detrimental impact that AI is having on the environment. Powering AI server farms and training AI data models consumes a sickening amount of energy, with studies warning that &lt;a href="https://www.bbc.co.uk/news/technology-67053139" target="_blank" rel="noopener"&gt;the AI industry could consume as much energy as a country the size of the Netherlands by 2027&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;There’s also the ethical implications of &lt;a href="https://english.elpais.com/economy-and-business/2024-01-01/the-hidden-labor-force-behind-chatgpt-the-drama-of-the-ghost-workers.html" target="_blank" rel="noopener"&gt;AI companies exploiting ghost workers&lt;/a&gt; to moderate content. The legal implications of &lt;a href="https://www.theguardian.com/technology/2024/jan/08/ai-tools-chatgpt-copyrighted-material-openai" target="_blank" rel="noopener"&gt;stealing copywrited material to train AI models&lt;/a&gt;. And the opportunity cost of &lt;a href="https://www.theguardian.com/technology/2024/mar/27/ai-apocalypse-could-take-away-almost-8m-jobs-in-uk-says-report" target="_blank" rel="noopener"&gt;loosing your job to AI&lt;/a&gt;. The list goes on.&lt;/p&gt;
&lt;p&gt;For these reasons, I’m right behind this “AI-free” idea and what it stands for.&lt;/p&gt;
&lt;p&gt;I like moving slowly, doing difficult things that take time and effort. There’s no better feeling than using your fallible head and hands to create original work. Ultimately, it’s rewarding. Work to be proud of.&lt;/p&gt;
&lt;p&gt;Can the same be said of the generative AI &lt;a href="https://en.wikipedia.org/wiki/Slop_%28artificial_intelligence%29" target="_blank" rel="noopener"&gt;slop&lt;/a&gt; that’s currently polluting the web? Probabaly not.&lt;/p&gt;
&lt;p&gt;Anyway, I hope to see this “AI-free” trend continue to grow. I’ll return to this post to document more examples, as and when I find them.&lt;/p&gt;
&lt;hr&gt;
&lt;p&gt;&lt;strong&gt;27th Aug 24&lt;/strong&gt;: &lt;a href="https://piccalil.li/about/" target="_blank" rel="noopener"&gt;Piccalilli&lt;/a&gt; commits to being an AI-free publication:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;We maintain that quality by focusing on web standards technology and a guaranteed commitment that &lt;mark&gt;we will never publish content written by large language models or other flavours of “AI”&lt;/mark&gt;.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;&lt;strong&gt;31st August 25&lt;/strong&gt;: &lt;a href="https://dbushell.com/2025/06/18/ai-policy/" target="_blank" rel="noopener"&gt;David Bushell&lt;/a&gt; breaks down some of the reasons his work is AI free in a &lt;a href="https://dbushell.com/ai/" target="_blank" rel="noopener"&gt;AI Policy&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;10th September 25&lt;/strong&gt;: Derek Sivers has a short statement in the footer of his website that reads:&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;Everything here is written by me, personally, not an AI.&lt;/p&gt;
&lt;/blockquote&gt;
&lt;p&gt;He also links to his &lt;a href="https://sive.rs/ai" target="_blank" rel="noopener"&gt;AI manifesto&lt;/a&gt; page, which details how he uses generative AI/LLM tools in his work.&lt;/p&gt;

&lt;p&gt;Last week I upgraded Hugo from &lt;a href="https://github.com/gohugoio/hugo/releases/tag/v0.115.0" target="_blank" rel="noopener"&gt;v0.115.0&lt;/a&gt; to &lt;a href="https://github.com/gohugoio/hugo/releases/tag/v0.126.1" target="_blank" rel="noopener"&gt;v0.126.1&lt;/a&gt;. With all the Hugo sites I manage (for myself and clients), I was half expecting a few breaking changes to fix as a result of the update.&lt;/p&gt;
&lt;p&gt;But this is Hugo we’re talking about here and &lt;a href="https://harrycresswell.com/writing/breaking-changes-upgrading-hugo/"&gt;I’ve been pleasantly surprised in the past&lt;/a&gt; at how few issues you tend to run into when upgrading versions. So I was feeling somewhat confident this time round.&lt;/p&gt;
&lt;p&gt;Luckily, all sites were unscathed by &lt;a href="https://github.com/gohugoio/hugo/releases?page=3" target="_blank" rel="noopener"&gt;the documented breaking changes of v.0.123.0&lt;/a&gt;, but there were a few warning and other minor things to fix, which I’ll run through in this post, along with the solutions.&lt;/p&gt;
&lt;h2 id="fixing-the-siteisserver-warning"&gt;Fixing the &lt;code&gt;.Site.IsServer&lt;/code&gt; warning&lt;/h2&gt;
&lt;p&gt;Following the upgrade, when running &lt;code&gt;hugo sever&lt;/code&gt; Hugo displayed the following warnings in the terminal.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;WARN Site.IsServer was deprecated in Hugo v0.120.0 and will be removed in a future release. Use hugo.IsServer instead.
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;With &lt;code&gt;.Site.IsServer&lt;/code&gt; now depreciated, the simple update, as suggested, is to use &lt;code&gt;hugo.IsServer&lt;/code&gt; instead.&lt;/p&gt;
&lt;h2 id="fixing-the-author-key-warning"&gt;Fixing the &lt;code&gt;Author&lt;/code&gt; key warning&lt;/h2&gt;
&lt;p&gt;Another warning shown when running &lt;code&gt;hugo server&lt;/code&gt; was the following:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;WARN The author key in site configuration is deprecated. Use params.author.name instead.
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;With author key now been depreciated, adding something like the following to your configuration file won’t be possible in upcoming releases.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-toml" data-lang="toml"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;Author&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;Harry Cresswell&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;github&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;https://github.com/harrycresswell&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;linkedin&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;https://linkedin.com/in/harrycresswell/&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Instead, Hugo wants you to treat author as a param:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-toml" data-lang="toml"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;author&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;name&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;Harry Cresswell&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;github&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;https://github.com/harrycresswell&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;linkedin&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;https://linkedin.com/in/harrycresswell/&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Inside your templates, what was once this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{ with .Site.Author.name }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;meta&lt;/span&gt; &lt;span class="na"&gt;name&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;author&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;content&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ . }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{ end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Become this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{ with .Site.Params.author.name }}
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;meta&lt;/span&gt; &lt;span class="na"&gt;name&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;author&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;content&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;{{ . }}&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;{{ end }}
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;With the warnings out of the way, there were two minor issues I ran into following the upgrade.&lt;/p&gt;
&lt;h2 id="finding-an-alternative-for-filepath"&gt;Finding an alternative for &lt;code&gt;File.Path&lt;/code&gt;&lt;/h2&gt;
&lt;p&gt;I haven’t quite worked out the reasons why yet, but for some reason Hugo v0.126.1 doesn’t seem to like the use of &lt;code&gt;.File.Path&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;I was using it on one of my sites to conditionally render a partial on certain pages.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;if&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;eq&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;File&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;Path&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;colour/_index.md&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;partial&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;colors.html&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I couldn’t seem to fix the issue using either the &lt;code&gt;.File&lt;/code&gt; or &lt;code&gt;.Path&lt;/code&gt; method, so I’ve resorted to using &lt;a href="https://gohugo.io/methods/resource/relpermalink/" target="_blank" rel="noopener"&gt;RelPermalink&lt;/a&gt; to check the relative permalink instead.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-go" data-lang="go"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="k"&gt;if&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;eq&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;RelPermalink&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;/logo/&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;partial&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="s"&gt;&amp;#34;logos.html&amp;#34;&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="w"&gt;&lt;/span&gt;&lt;span class="p"&gt;{{&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="nx"&gt;end&lt;/span&gt;&lt;span class="w"&gt; &lt;/span&gt;&lt;span class="p"&gt;}}&lt;/span&gt;&lt;span class="w"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I actually prefer the solution above. It achieves the exact same results and it’s easier to make sense of.&lt;/p&gt;
&lt;h2 id="custom-params-on-language-top-level"&gt;Custom params on language top level&lt;/h2&gt;
&lt;p&gt;The final issue I ran into was another depreciation which caused a build error. This time encountered when serving sites which use Hugo’s &lt;a href="https://gohugo.io/content-management/multilingual/" target="_blank" rel="noopener"&gt;Multilingual mode&lt;/a&gt;.&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;ERROR deprecated: config: languages.en.languagenameshort: custom params on the language top level was deprecated in Hugo v0.112.0 and will be removed in Hugo 0.127.0. Put the value below [languages.en.params]. See https://gohugo.io/content-management/multilingual/#changes-in-hugo-01120
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Similar to the Author key above, Hugo now wants us to move &lt;code&gt;languageNameShort&lt;/code&gt; from the top level of the language config to the param level.&lt;/p&gt;
&lt;p&gt;In other words, something like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-toml" data-lang="toml"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;languages&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;languages&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;en&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;languageName&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;United Kingdom&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;languageCode&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;en-gb&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;languageNameShort&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;uk&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;contentDir&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;content/en&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;weight&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="mi"&gt;10&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Needs to be rewritten like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-toml" data-lang="toml"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;languages&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;languages&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;en&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;languageName&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;United Kingdom&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;languageCode&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;en-gb&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;contentDir&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;content/en&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;weight&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="mi"&gt;10&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;[&lt;/span&gt;&lt;span class="nx"&gt;languages&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;en&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nx"&gt;params&lt;/span&gt;&lt;span class="p"&gt;]&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nx"&gt;languageNameShort&lt;/span&gt; &lt;span class="p"&gt;=&lt;/span&gt; &lt;span class="s2"&gt;&amp;#34;uk&amp;#34;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Nothing too challenging about that.&lt;/p&gt;
&lt;h2 id="final-words"&gt;Final words&lt;/h2&gt;
&lt;p&gt;I’m always impressed at how straight forward it is to maintain Hugo websites and upgrade to the latest releases of the Hugo binary. This is testament to Hugo and its built-in features, which typically means there’s little need to rely on third party dependencies.&lt;/p&gt;
&lt;p&gt;I don’t always upgrade Hugo to the latest version, but there were two new features released in v0.126.0 that I’m keen to check out. The first is the long-awaited &lt;a href="https://gohugo.io/content-management/content-adapters/" target="_blank" rel="noopener"&gt;Content Adapters&lt;/a&gt;, which allows you to build pages from a remote data source. The second is the introduction of the &lt;a href="https://gohugo.io/getting-started/configuration-markup/#extras-extension" target="_blank" rel="noopener"&gt;Goldmark Extras extension&lt;/a&gt;, which enables inserted text, mark text, subscript and superscript. A subtle improvement to markdown authoring, but one I’m particularly excited about. Now I no longer have to misuse &lt;code&gt;&amp;lt;em&amp;gt;&lt;/code&gt; to add text highlighting in markdown content. Instead, I can ==surround text in two sets of equal signs== to create &lt;mark&gt;highlighted text&lt;/mark&gt;.&lt;/p&gt;

&lt;p&gt;Since formalising &lt;a href="https://cu.harrycresswell.com/" target="_blank" rel="noopener"&gt;my CSS boilerplate&lt;/a&gt;, I’ve come to realise that many CSS problems that I’d typically solved with unique component based styles (using BEM or similar), can actually be solved using a handful of reusable styles.&lt;/p&gt;
&lt;p&gt;For this I have &lt;a href="https://cube.fyi/" target="_blank" rel="noopener"&gt;CUBE CSS&lt;/a&gt; and techniques from &lt;a href="https://every-layout.dev/" target="_blank" rel="noopener"&gt;Every Layout&lt;/a&gt; to thank.&lt;/p&gt;
&lt;p&gt;With this in mind, last week I set out to rewrite the CSS on my personal site with the aim of cleaning up redundant styles and make some general improvements. In the process I managed to reduce the file size of my stylesheet by 33% – all with virtually zero change to the overall design.&lt;/p&gt;
&lt;p&gt;Maybe this isn’t much to shout about when it concerns some low-traffic personal website, but this would be a big deal for a commercial website. Savings like this can improve site speed, increase conversion and ultimately lead to more sales. And all it took was a simple audit of my styles and a slight rethink about how I write CSS.&lt;/p&gt;
&lt;p&gt;Let me explain what’s changed and how I’ve managed to achieve the exact same results from 134 fewer CSS rules.&lt;/p&gt;
&lt;h2 id="removing-unused-styles"&gt;Removing unused styles&lt;/h2&gt;
&lt;p&gt;First I combed through my Hugo templates and CSS files to remove any unused styles, most of which were probably the remnants of some previously abandoned feature.&lt;/p&gt;
&lt;p&gt;Maybe I could have used a tool to help with this step, but with a modest amount of styles, I decided to keep things simple and do this bit by hand.&lt;/p&gt;
&lt;p&gt;The process involved working through my stylesheets and comparing them with my template files to see which classes were no longer being used, then removing any unused styles. Nothing glamorous, just good old grunt work.&lt;/p&gt;
&lt;h2 id="improving-site-wide-variables-by-adopting-utopia"&gt;Improving site wide variables by adopting Utopia&lt;/h2&gt;
&lt;p&gt;Next I set out to improve the &lt;a href="https://developer.mozilla.org/en-US/docs/Web/CSS/:root#declaring_global_css_variables" target="_blank" rel="noopener"&gt;global variables&lt;/a&gt; which permeate through the rest of my CSS.&lt;/p&gt;
&lt;p&gt;First I converted all SASS variables to &lt;a href="https://developer.mozilla.org/en-US/docs/Web/CSS/Using_CSS_custom_properties" target="_blank" rel="noopener"&gt;CSS custom properties&lt;/a&gt;. Next I changed my Utopia fluid type scale &lt;a href="https://github.com/harrycresswell/harry/commit/b5202cbaf11450413a137a07d698306248ac5b15" target="_blank" rel="noopener"&gt;from CSS locks to CSS Clamp&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;Following this, I ditched my old SASS based spacing system in favour of, you guess it, a fluid responsive scale using &lt;a href="https://utopia.fyi/" target="_blank" rel="noopener"&gt;Utopia&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;Now, both type and spacing on my site resizes fluidly depending on viewport width or device. To make this happen, Utopia makes heavy use of the Clamp function, providing a scale of Custom Properties.&lt;/p&gt;
&lt;p&gt;For type:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;root&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--step--1&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;0.8889&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.8455&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.2168&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--step-0&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.9024&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.4878&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.25&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--step-1&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;1.125&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.9543&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.8537&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.5625&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--step-2&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;1.2656&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.9973&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;1.3415&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.9531&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--step-3&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;1.4238&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.0267&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;1.9855&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;2.4414&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--step-4&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;1.6018&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.036&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;2.8292&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;3.0518&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;And for space:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;root&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-3xs&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;0.25&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.2283&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.1087&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.3125&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-2xs&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;0.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.4565&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.2174&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.625&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-xs&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;0.75&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.6848&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.3261&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.9375&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-s&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;0.913&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.4348&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.25&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-m&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mf"&gt;1.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.3696&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.6522&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.875&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-l&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;2&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;1.8261&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;0.8696&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;2.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-xl&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;3&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;2.7391&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;1.3043&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;3.75&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-2xl&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;4&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;3.6522&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;1.7391&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-3xl&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;6&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;5.4783&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;2.6087&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;7.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--space-4xl&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;7&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;6.3913&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="mf"&gt;3.0435&lt;/span&gt;&lt;span class="n"&gt;vi&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mf"&gt;8.75&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Now, instead of having to write a breakpoint to adjust type or space sizing for smaller devices like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;section&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;2&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;2&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;@&lt;/span&gt;&lt;span class="k"&gt;media&lt;/span&gt; &lt;span class="o"&gt;(&lt;/span&gt;&lt;span class="nt"&gt;min-width&lt;/span&gt;&lt;span class="o"&gt;:&lt;/span&gt; &lt;span class="nt"&gt;768px&lt;/span&gt;&lt;span class="o"&gt;)&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;section&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;4&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;4&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I can now do something like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;section&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="n"&gt;margin-block&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="mi"&gt;-3&lt;/span&gt;&lt;span class="n"&gt;xs&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;And utopia handles the resizing fluidly as the browser width or device size changes.&lt;/p&gt;
&lt;p&gt;With the additional switch to using &lt;a href="https://developer.mozilla.org/en-US/docs/Web/CSS/CSS_logical_properties_and_values" target="_blank" rel="noopener"&gt;Logic Properties&lt;/a&gt; where possible, all this good work resulted in far fewer lines of CSS.&lt;/p&gt;
&lt;h2 id="move-as-much-css-a-possible-to-global-styles"&gt;Move as much CSS a possible to global styles&lt;/h2&gt;
&lt;p&gt;After improving my variables, the next step was to move as much of my CSS as possible to global styles.&lt;/p&gt;
&lt;p&gt;Global styles aim to broadly style as much as possible. This is done by targeting HTML elements, using &lt;a href="https://developer.mozilla.org/en-US/docs/Web/CSS/Type_selectors" target="_blank" rel="noopener"&gt;Type selectors&lt;/a&gt;. The result of creating global styles is HTML that looks great without any additional classes. This is the exact approach used by classless CSS frameworks like &lt;a href="https://watercss.kognise.dev/" target="_blank" rel="noopener"&gt;Water.CSS&lt;/a&gt;, &lt;a href="https://concrete.style/" target="_blank" rel="noopener"&gt;Concrete.css&lt;/a&gt; and &lt;a href="https://simplecss.org/" target="_blank" rel="noopener"&gt;Simple.css&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;Notable improvements to my global styles include removing the margin from headings, which is now handled using the &lt;code&gt;.flow&lt;/code&gt; utility. But more on that later.&lt;/p&gt;
&lt;p&gt;Similarly, I’ve also managed to ditch many of the utility classes I was using to create the various link styles on my site. I now style the &lt;code&gt;&amp;lt;a&amp;gt;&lt;/code&gt; element globally and handle each style variant by creating &lt;a href="https://harrycresswell.com/writing/reducing-css/#create-exceptions-for-state-and-style-modifiers"&gt;Exceptions&lt;/a&gt; using data attributes.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;brand&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;underline&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;&amp;amp;:hover&lt;/span&gt; &lt;span class="err"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt; &lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;bg&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;background-color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;brand&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;none&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="err"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="c"&gt;/* Use naked variant to style links as text*/&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;data-variant&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;naked&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;text&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;none&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;data-variant&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;naked&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;hover&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;background-color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;unset&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;underline&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="c"&gt;/* Use desaturate to remove brand color from links */&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;data-variant&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;desaturate&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;text&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;data-variant&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;desaturate&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;hover&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;background-color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;text&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;bg&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;none&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I’ve also moved button styles to the &lt;code&gt;&amp;lt;button&amp;gt;&lt;/code&gt; element and input elements with type &lt;code&gt;submit&lt;/code&gt;, &lt;code&gt;button&lt;/code&gt; or &lt;code&gt;reset&lt;/code&gt;. I’ve included the &lt;code&gt;.button&lt;/code&gt; class to make it easier to style links as buttons.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;button&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;button&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;input&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;type&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;submit&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;],&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;input&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;type&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;reset&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;],&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;input&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;type&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s2"&gt;&amp;#34;button&amp;#34;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;background-color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;text&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;border&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;solid&lt;/span&gt; &lt;span class="mf"&gt;.2&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;text&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;border-radius&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;border&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;radius&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;color&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;color&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;bg&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;cursor&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;pointer&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;padding&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="mi"&gt;-3&lt;/span&gt;&lt;span class="n"&gt;xs&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;s&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;inline-block&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-decoration&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;none&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;text-align&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;left&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;white-space&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;nowrap&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Before now, I only had these styles set up on a &lt;code&gt;.button&lt;/code&gt; class, meaning that the HTML elements targeted in the code above would inherit the default user-agent styles by default. That is, unless the &lt;code&gt;.button&lt;/code&gt; class was applied. So although I haven’t reduced the amount of CSS, I’ve definitely made some improvements when it comes to creating consistency between elements.&lt;/p&gt;
&lt;p&gt;Though not strictly reducing the amount of CSS, the final thing I did was to consolidate my global styles into fewer SASS partial files.&lt;/p&gt;
&lt;p&gt;So this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;scss&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;base&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;code&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;form&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;heading&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;image&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;link&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;list&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;quote&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;reset&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;syntax&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_base&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;video&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Has become this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;scss&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;base&lt;/span&gt;&lt;span class="o"&gt;/&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_button&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_code&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_form&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_global&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_nav&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nt"&gt;_reset&lt;/span&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;scss&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Of course, I could have used a single partial file for all global styles, but I like to distinguish certain styles to improve reusability of code between projects. For example, not all website projects I work on will use forms, so by keeping the CSS used for forms inside a &lt;code&gt;_form.scss&lt;/code&gt; partial, I can easily remove form styles for that particular project.&lt;/p&gt;
&lt;p&gt;It’s worth mentioning that this is the only feature I now require SASS for – to break my CSS into partial files, then import them all into a &lt;code&gt;main.scss&lt;/code&gt; file.&lt;/p&gt;
&lt;h2 id="solve-common-layout-problems-with-intrinsically-responsive-and-reusable-layout-compositions"&gt;Solve common layout problems with intrinsically responsive (and reusable) layout compositions&lt;/h2&gt;
&lt;p&gt;One of the biggest improvements I’ve made to my CSS was adopting the concept of Compositions from &lt;a href="https://every-layout.dev/" target="_blank" rel="noopener"&gt;Every Layout&lt;/a&gt; and &lt;a href="https://cube.fyi/" target="_blank" rel="noopener"&gt;CUBE CSS&lt;/a&gt;.&lt;/p&gt;
&lt;p&gt;Compositions are skeletal styles that create flexible layouts containers for content. Importantly, they are reusable, not tied to any specific context and don’t depend on the use of media queries. Instead, they adapt and reconfigure themselves to fit any context or device.&lt;/p&gt;
&lt;p&gt;In the past, I’d often handle layout problems like this on the component level. For example, to style my navigation layout I might have created a component like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Nav&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;gap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nb"&gt;calc&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt; &lt;span class="o"&gt;*&lt;/span&gt;&lt;span class="mf"&gt;1.5&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Then, to style the layout of page pagination items I might create another component with very similar styles:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Pagination&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-wrap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;wrap&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;This would result in lots of repetition, where I’d have several different components, each with virtually the same CSS.&lt;/p&gt;
&lt;p&gt;With compositions, I can avoid this repetition by creating context agnostic layouts. These layouts can be used in any context that requires such a layout.&lt;/p&gt;
&lt;p&gt;Take the &lt;code&gt;.cluster&lt;/code&gt; composition as an example, which creates flex items which &lt;em&gt;cluster&lt;/em&gt; inline, until space no longer permits.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;cluster&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-wrap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;wrap&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;gap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;xs&lt;/span&gt;&lt;span class="p"&gt;));&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;justify-content&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;cluster&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;horizontal&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;alignment&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="kc"&gt;flex-start&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;align-items&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;cluster&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;vertical&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;alignment&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="kc"&gt;center&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I’m now using &lt;code&gt;.cluster&lt;/code&gt; to create a flexible layouts for my &lt;em&gt;navigation&lt;/em&gt;, &lt;em&gt;page pagination&lt;/em&gt;, &lt;em&gt;taxonomy tags&lt;/em&gt; and &lt;em&gt;post author&lt;/em&gt; component. Using one CSS class, I can now tackle many similar layout problems found in various different places.&lt;/p&gt;
&lt;h2 id="use-utility-classes-for-specific-problems-that-global-styles-and-compositions-dont-solve"&gt;Use utility classes for specific problems that global styles and compositions don’t solve&lt;/h2&gt;
&lt;p&gt;In a utility-first approach, utility classes make up the majority of styles and are the primary method of applying styles to HTML. If you’ve used Tailwind before, then you will be familiar with this approach.&lt;/p&gt;
&lt;p&gt;To be clear, I’m not using utility classes in this way.&lt;/p&gt;
&lt;p&gt;Instead, I use utility classes is to solve specific problems that my compositions or global CSS styles don’t solve. In other words, they aren’t the first thing I reach for, but something to lean on when nothing else solves the problem.&lt;/p&gt;
&lt;p&gt;Two such utilities have made a big impact in reducing the amount of CSS my site design requires. Both solve problems concerning the space between elements.&lt;/p&gt;
&lt;p&gt;The first is the &lt;code&gt;.flow&lt;/code&gt; utility, which creates consistent space and rhythm between elements.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;flow&lt;/span&gt; &lt;span class="o"&gt;&amp;gt;&lt;/span&gt; &lt;span class="o"&gt;*&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="o"&gt;*&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;flow&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;em&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;This technique was popularised as &lt;a href="https://every-layout.dev/layouts/stack/" target="_blank" rel="noopener"&gt;The Stack&lt;/a&gt; layout by Heydon Pickering and Andy Bell in Every Layout. It works by applying margin to direct sibling elements. Flow is incredibly useful for creating space between prose elements, for example the headings and paragraphs found in a blog post.&lt;/p&gt;
&lt;p&gt;With liberal use of &lt;code&gt;.flow&lt;/code&gt;, I’ve been able to remove the styles I’d typically use to create space between elements. For example, I no longer need to litter my base styles with the &lt;a href="https://developer.mozilla.org/en-US/docs/Web/CSS/CSS_selectors/Selectors_and_combinators#next-sibling_combinator" target="_blank" rel="noopener"&gt;next-sibling combinator&lt;/a&gt;:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;ul&lt;/span&gt;&lt;span class="o"&gt;+&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;ol&lt;/span&gt;&lt;span class="o"&gt;+&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;pre&lt;/span&gt;&lt;span class="o"&gt;+&lt;/span&gt;&lt;span class="nt"&gt;p&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Similarly, using the &lt;code&gt;.flow&lt;/code&gt; utility has also meant I can remove the default margin from heading elements:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;h2&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;step&lt;/span&gt;&lt;span class="mi"&gt;-2&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;2.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;h3&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;h3&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;step&lt;/span&gt;&lt;span class="mi"&gt;-1&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;1.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;h4&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;h4&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;step&lt;/span&gt;&lt;span class="mi"&gt;-0&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;line-height&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;1.4&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;1.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Instead, I can now use the &lt;code&gt;--flow-space&lt;/code&gt; variable to override the default &lt;a href="https://developer.mozilla.org/en-US/docs/Web/CSS/Using_CSS_custom_properties#custom_property_fallback_values" target="_blank" rel="noopener"&gt;fallback value&lt;/a&gt; and control the amount of margin required:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;h2&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;h2&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;step&lt;/span&gt;&lt;span class="mi"&gt;-2&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--flow-space&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;l&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;xl&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;h3&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;h3&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;step&lt;/span&gt;&lt;span class="mi"&gt;-1&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--flow-space&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;l&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="nt"&gt;h4&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;h4&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;font-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;step&lt;/span&gt;&lt;span class="mi"&gt;-0&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;line-height&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;1.4&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--flow-space&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;m&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;The second utility I’m using to reduce CSS is &lt;code&gt;.region&lt;/code&gt;, which has the sole job of adding consistent vertical padding between sections of content.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;region&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;padding-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;region&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;l&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;xl&lt;/span&gt;&lt;span class="p"&gt;));&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;padding-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;region&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;l&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;xl&lt;/span&gt;&lt;span class="p"&gt;));&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Before using the &lt;code&gt;.region&lt;/code&gt; utility, I would typically created space between elements on a component level. Consider my old &lt;code&gt;.Hero&lt;/code&gt; styles as an example:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Hero&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;padding-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;6&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;padding-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;6&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;With the reusable &lt;code&gt;.region&lt;/code&gt; utility, I can achive the exact same results in a variety of places. For example, my &lt;em&gt;site header&lt;/em&gt;, &lt;em&gt;site footer&lt;/em&gt; and &lt;em&gt;newsletter signup form&lt;/em&gt; all rely on the &lt;code&gt;.region&lt;/code&gt; class for their vertical spacing.&lt;/p&gt;
&lt;p&gt;With considered use of utilities I’ve been able to make some big reductions in the amount of CSS required to style my site. However, utilities don’t always provide the solution, particularly when it comes to more complex UI.&lt;/p&gt;
&lt;h2 id="create-blocks-and-combine-with-utilities-for-more-complex-pieces-of-ui"&gt;Create blocks (and combine with utilities) for more complex pieces of UI&lt;/h2&gt;
&lt;p&gt;According to &lt;a href="https://cube.fyi/block.html#block" target="_blank" rel="noopener"&gt;cube.fyi&lt;/a&gt;: “A block is a skeletal component or organisational structure. To compare it to common user interface elements: it is a card element or a button element.”&lt;/p&gt;
&lt;p&gt;I’m now using blocks to manage the styles of commonly used design patterns, which global styles, compositions and utilities don’t necessarily account for.&lt;/p&gt;
&lt;p&gt;Before now I’d create block components using SUITCSS, a BEM-like syntax, for all sorts of UI problems:&lt;/p&gt;
&lt;pre tabindex="0"&gt;&lt;code&gt;.Block {}
.Block-part {}
.Block—modifier {}
&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;Consider my newsletter component, as an example.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;NewsletterForm&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-direction&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;column&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;NewsletterForm-field&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;&amp;amp;:nth-child(even)&lt;/span&gt; &lt;span class="err"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="err"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;@&lt;/span&gt;&lt;span class="k"&gt;media&lt;/span&gt; &lt;span class="o"&gt;(&lt;/span&gt;&lt;span class="nt"&gt;min-width&lt;/span&gt;&lt;span class="o"&gt;:&lt;/span&gt; &lt;span class="nt"&gt;768px&lt;/span&gt;&lt;span class="o"&gt;)&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;NewsletterForm&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-direction&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;row&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;NewsletterForm-label&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-top&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;0&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;NewsletterForm-field&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;align-self&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex-end&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="err"&gt;&amp;amp;:nth-child(even)&lt;/span&gt; &lt;span class="err"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-bottom&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;0&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="o"&gt;&amp;amp;&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;not&lt;/span&gt;&lt;span class="o"&gt;(&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;last-of-type&lt;/span&gt;&lt;span class="o"&gt;)&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;margin-right&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mf"&gt;.5&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="err"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Now, all this is handled by &lt;code&gt;.switcher&lt;/code&gt;, a reusable layout composition, which can be applied to any context. Like most of the layout compositions I find myself using, &lt;a href="https://every-layout.dev/layouts/switcher/" target="_blank" rel="noopener"&gt;Switcher&lt;/a&gt; was popularised in Every Layout.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;switcher&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-wrap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;wrap&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;gap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;gutter&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;align-items&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;switcher&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;vertical&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;alignment&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="kc"&gt;flex-start&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;switcher&lt;/span&gt; &lt;span class="o"&gt;&amp;gt;&lt;/span&gt; &lt;span class="o"&gt;*&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-grow&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-basis&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nb"&gt;calc&lt;/span&gt;&lt;span class="p"&gt;((&lt;/span&gt;&lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;switcher&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;target&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;container&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;width&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;40&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt; &lt;span class="o"&gt;-&lt;/span&gt; &lt;span class="mi"&gt;100&lt;/span&gt;&lt;span class="kt"&gt;%&lt;/span&gt;&lt;span class="p"&gt;)&lt;/span&gt; &lt;span class="o"&gt;*&lt;/span&gt; &lt;span class="mi"&gt;999&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;switcher&lt;/span&gt; &lt;span class="o"&gt;&amp;gt;&lt;/span&gt; &lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;nth-last-child&lt;/span&gt;&lt;span class="o"&gt;(&lt;/span&gt;&lt;span class="nt"&gt;n&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="nt"&gt;5&lt;/span&gt;&lt;span class="o"&gt;),&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;switcher&lt;/span&gt; &lt;span class="o"&gt;&amp;gt;&lt;/span&gt; &lt;span class="p"&gt;:&lt;/span&gt;&lt;span class="nd"&gt;nth-last-child&lt;/span&gt;&lt;span class="o"&gt;(&lt;/span&gt;&lt;span class="nt"&gt;n&lt;/span&gt; &lt;span class="o"&gt;+&lt;/span&gt; &lt;span class="nt"&gt;5&lt;/span&gt;&lt;span class="o"&gt;)&lt;/span&gt; &lt;span class="o"&gt;~&lt;/span&gt; &lt;span class="o"&gt;*&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;flex-basis&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;100&lt;/span&gt;&lt;span class="kt"&gt;%&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;By using &lt;code&gt;.switcher&lt;/code&gt; alongside the block class, like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;form&lt;/span&gt; &lt;span class="na"&gt;class&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;NewsletterForm switcher&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; ...
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;form&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;I’ve been able to simplify the component block itself to a single line of CSS:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;NewsletterForm&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--switcher-vertical-alignment&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;flex-end&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;In this case, all the block class contains is a single custom property used to override the default vertical alignment of &lt;code&gt;flex-start&lt;/code&gt; with &lt;code&gt;flex-end&lt;/code&gt;.&lt;/p&gt;
&lt;p&gt;By using this approach, I’ve been able to simplify the complexity of most of the block components on my site. And, in certain cases, I’ve managed to remove the need to create block classes entirely. This has obviously lead to a considerable reduction in the amount of CSS required to style my website.&lt;/p&gt;
&lt;h2 id="create-exceptions-for-state-and-style-modifiers"&gt;Create Exceptions for state and style modifiers&lt;/h2&gt;
&lt;p&gt;According to &lt;a href="https://cube.fyi/exception.html" target="_blank" rel="noopener"&gt;cube.fyi&lt;/a&gt;, “An exception is a deviation from the rules outlined in a block“, however, personally, I’m also using exceptions for deviations in compositions and – in the case of links (see global styles section above) – global styles.&lt;/p&gt;
&lt;p&gt;In my CSS, exceptions deal with changes in state or style of an element. They replace BEM/SUITCSS modifiers. For example, with SUITCSS, I might have modified the default button style by adding a &lt;code&gt;Button--secondary&lt;/code&gt; class.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="na"&gt;class&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;Button Button--secondary&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;#&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Reply by email&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;This modifier class would have changed the button style from the default style to a secondary style.&lt;/p&gt;
&lt;p&gt;Using CUBE CSS Exceptions, however, it’s possible to achieve the same results using data attributes which override or extend the default styles.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-html" data-lang="html"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;&amp;lt;&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt; &lt;span class="na"&gt;class&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;button&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;data-variant&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;secondary&amp;#34;&lt;/span&gt; &lt;span class="na"&gt;href&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s"&gt;&amp;#34;#&amp;#34;&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;Reply by email&lt;span class="p"&gt;&amp;lt;/&lt;/span&gt;&lt;span class="nt"&gt;a&lt;/span&gt;&lt;span class="p"&gt;&amp;gt;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;While this doesn’t necessarily reduce the amount of CSS I’m writing, it certainly reduces the number of classes required and improves clarity in my templates. It also, to quote the &lt;a href="https://cube.fyi/exception.html" target="_blank" rel="noopener"&gt;cube.fyi&lt;/a&gt; docs again, “provides a useful hook for both CSS and JavaScript.”&lt;/p&gt;
&lt;p&gt;But, the real magic happens when combining exceptions with compositions. Remember, compositions create flexible layout without the need for media queries to adapt to different contexts. And with fewer media queries, the amount of CSS required to solve layout problems is greatly reduced.&lt;/p&gt;
&lt;p&gt;For example, the CSS for the grid layout I use on certain case study pages now looks like this:&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;grid&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="k"&gt;grid&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;grid-template-columns&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;repeat&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;grid&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;placement&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="kc"&gt;auto&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="kc"&gt;fill&lt;/span&gt;&lt;span class="p"&gt;),&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nf"&gt;minmax&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;grid&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;min&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;item&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;size&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;16&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;),&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="n"&gt;fr&lt;/span&gt;&lt;span class="p"&gt;));&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;gap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="n"&gt;gutter&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="nf"&gt;var&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="o"&gt;--&lt;/span&gt;&lt;span class="kc"&gt;space&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;s&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;l&lt;/span&gt;&lt;span class="p"&gt;));&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;grid&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;data-layout&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s1"&gt;&amp;#39;50-50&amp;#39;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--grid-placement&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;auto&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;fit&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--grid-min-item-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;16&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;50&lt;/span&gt;&lt;span class="kt"&gt;vw&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;26&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;grid&lt;/span&gt;&lt;span class="o"&gt;[&lt;/span&gt;&lt;span class="nt"&gt;data-layout&lt;/span&gt;&lt;span class="o"&gt;=&lt;/span&gt;&lt;span class="s1"&gt;&amp;#39;25x4&amp;#39;&lt;/span&gt;&lt;span class="o"&gt;]&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--grid-placement&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="kc"&gt;auto&lt;/span&gt;&lt;span class="o"&gt;-&lt;/span&gt;&lt;span class="n"&gt;fit&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="nv"&gt;--grid-min-item-size&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;clamp&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;10&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;20&lt;/span&gt;&lt;span class="kt"&gt;vw&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;26&lt;/span&gt;&lt;span class="kt"&gt;rem&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;p&gt;Whereas, before I achieved the same results with a mix of BEM style &lt;em&gt;block&lt;/em&gt;, &lt;em&gt;element&lt;/em&gt; and &lt;em&gt;modifier&lt;/em&gt; classes, along with breakpoints to handle the layout change from smaller to larger viewports.&lt;/p&gt;
&lt;div class="highlight"&gt;&lt;pre tabindex="0" class="chroma"&gt;&lt;code class="language-css" data-lang="css"&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Grid&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;display&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="k"&gt;grid&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;grid-gap&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="mi"&gt;24&lt;/span&gt;&lt;span class="kt"&gt;px&lt;/span&gt;&lt;span class="p"&gt;;&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Grid--2col&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="k"&gt;grid-template-columns&lt;/span&gt;&lt;span class="p"&gt;:&lt;/span&gt; &lt;span class="nf"&gt;repeat&lt;/span&gt;&lt;span class="p"&gt;(&lt;/span&gt;&lt;span class="mi"&gt;2&lt;/span&gt;&lt;span class="p"&gt;,&lt;/span&gt; &lt;span class="mi"&gt;1&lt;/span&gt;&lt;span class="n"&gt;fr&lt;/span&gt;&lt;span class="p"&gt;);&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;}&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt;&lt;span class="p"&gt;@&lt;/span&gt;&lt;span class="k"&gt;media&lt;/span&gt; &lt;span class="o"&gt;(&lt;/span&gt;&lt;span class="nt"&gt;min-width&lt;/span&gt;&lt;span class="o"&gt;:&lt;/span&gt; &lt;span class="nt"&gt;768px&lt;/span&gt;&lt;span class="o"&gt;)&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Grid&lt;/span&gt;&lt;span class="o"&gt;,&lt;/span&gt;
&lt;/span&gt;&lt;/span&gt;&lt;span class="line"&gt;&lt;span class="cl"&gt; &lt;span class="p"&gt;.&lt;/span&gt;&lt;span class="nc"&gt;Grid--2col&lt;/span&gt; &lt;span class="p"&gt;{&lt;/span&gt;
