&lt;pre class="highlight plaintext"&gt;&lt;code&gt;for num in main:
ball = tk.Label(
self.numbers_frame,
text=str(num),
bg="#1E90FF",
fg="white",
font=("Segoe UI", 16, "bold"),
width=4,
height=2,
relief="raised",
bd=2
)
ball.pack(side="left", padx=5)
&lt;/code&gt;&lt;/pre&gt;

